<html>
<head>
  <title>BloomTube</title>
  <style>
    /* include your CSS styles here */
  </style>
</head>
<body>
  <h1>Welcome to BloomTube</h1>
  <div id="player"></div>
  <div id="info"></div>

  <script src="https://www.youtube.com/iframe_api"></script>
  <script>
    const data = [
      // include your data array here
    ];

    const randomSong = data[Math.floor(Math.random() * data.length)];

    function onYouTubeIframeAPIReady() {
      new YT.Player('player', {
        height: '360',
        width: '640',
        videoId: randomSong.url.split('v=')[1],
        events: {
          'onReady': onPlayerReady
        }
      });
    }

    function onPlayerReady(event) {
      event.target.playVideo();
      document.getElementById('info').innerHTML = `
        <h2>${randomSong.song} by ${randomSong.artist}</h2>
        <p><strong>Genre:</strong> ${randomSong.genre}, <strong>Release Year:</strong> ${randomSong.releaseYear}</p>
        <p><strong>Character Strength:</strong> ${randomSong.characterStrength}</p>
        <p><strong>Reasoning:</strong> ${randomSong.reasoning}</p>
        <p><strong>Tags:</strong> ${randomSong.tags.join(', ')}</p>
      `;
    }
  </script>
</body>
</html>
