  Widget _buildProgressVisualization(PrayerProvider prayerProvider) {
    List<charts.Series<PrayerData, String>> seriesList = [];

    for (var prayer in prayerProvider.prayers) {
      int totalPrayers = prayerProvider.getTotalPrayersForPrayerType(prayer);
      int completedPrayers =
          prayerProvider.getCompletedPrayersForPrayerType(prayer);

      seriesList.add(
        charts.Series<PrayerData, String>(
          id: prayer,
          domainFn: (PrayerData data, _) => data.prayer,
          measureFn: (PrayerData data, _) => data.count,
          data: [
            PrayerData(prayer, completedPrayers),
            PrayerData(prayer, totalPrayers - completedPrayers),
          ],
        ),
      );
    }

    return SizedBox(
      height: 200.h,
      child: charts.BarChart(
        seriesList,
        animate: true,
        barGroupingType: charts.BarGroupingType.grouped,
        defaultRenderer: charts.BarRendererConfig(
          groupingType: charts.BarGroupingType.grouped,
          strokeWidthPx: 1.0,
        ),
        primaryMeasureAxis: charts.NumericAxisSpec(
          tickProviderSpec: charts.BasicNumericTickProviderSpec(
            desiredTickCount: 5,
          ),
        ),
      ),
    );
