import 'hive_service.dart';
import 'package:share_plus/share_plus.dart';

class PrayerService {
  final HiveService _hiveService;
  bool isDataLoaded = false;
  final List<String> prayers = ['Fajr', 'Dhuhr', 'Asr', 'Maghrib', 'Isha'];
  Map<String, dynamic> prayerData = {};

  PrayerService(this._hiveService);

  Future<void> loadData() async {
    var loadedData = await _hiveService.loadData('prayerData');

    if (loadedData != null && loadedData is Map) {
      prayerData =
          Map<String, dynamic>.from(loadedData.cast<String, dynamic>());
    } else {
      _setDefaultPrayerData();
      savePrayerData();
    }

    isDataLoaded = true;
    print('Loaded data from Hive: $prayerData');
  }

  Future<void> createDetailedBreakdown(
      int startYear, int endYear, Map<int, int> percentageMissedByYear) async {
    prayerData['yearlyData'] = {};
    prayerData['monthlyData'] = {};

    for (int year = startYear; year <= endYear; year++) {
      prayerData['yearlyData'][year.toString()] = {
        for (var prayer in prayers)
          prayer: {
            'percentMissed': percentageMissedByYear[year]!,
            'prayersCompleted': _getTotalPrayersForYear(year) -
                (_getTotalPrayersForYear(year) *
                        percentageMissedByYear[year]! /
                        100)
                    .round(),
            'prayersMissed': (_getTotalPrayersForYear(year) *
                    percentageMissedByYear[year]! /
                    100)
                .round(),
          }
      };
    }
    await savePrayerData();
  }

  Future<void> updatePrayerData(
      int year, Map<String, dynamic> yearlyData) async {
    prayerData['yearlyData'][year.toString()] = yearlyData;
    await savePrayerData();
  }

  int getTotalPrayersPossible(int startYear, int endYear) {
    return (endYear - startYear + 1) * 365 * prayers.length;
  }

  int getCompletedPrayersForPrayerType(String prayer) {
    int completedPrayers = 0;
    for (var year = prayerData['startYear'];
        year <= prayerData['endYear'];
        year++) {
      completedPrayers += prayerData['yearlyData'][year.toString()][prayer]
          ['prayersCompleted'] as int;
    }
    return completedPrayers;
  }

  int getTotalPrayersForPrayerType(String prayer) {
    int totalPrayers = 0;
    for (var year = prayerData['startYear'];
        year <= prayerData['endYear'];
        year++) {
      totalPrayers += prayerData['yearlyData'][year.toString()][prayer]
                  ['prayersCompleted'] +
              prayerData['yearlyData'][year.toString()][prayer]['prayersMissed']
          as int;
    }
    return totalPrayers;
  }

  Future<void> incrementPrayer(String prayer) async {
    for (int year = prayerData['startYear'];
        year <= prayerData['endYear'];
        year++) {
      var prayerDataForYear = prayerData['yearlyData'][year.toString()][prayer];
      if (prayerDataForYear['prayersCompleted'] <
          _getTotalPrayersForYear(year)) {
        prayerDataForYear['prayersCompleted']++;
        prayerDataForYear['prayersMissed'] = _getTotalPrayersForYear(year) -
            prayerDataForYear['prayersCompleted'];
        prayerDataForYear['percentMissed'] =
            (prayerDataForYear['prayersMissed'] /
                    (_getTotalPrayersForYear(year)) *
                    100)
                .round();

        await savePrayerData();
        break; // Break after updating the first incomplete year
      }
    }
  }

  Future<void> savePrayerData() async {
    await _hiveService.saveData('prayerData', prayerData);
    print('Saved data to Hive: $prayerData');
  }

  void _setDefaultPrayerData() {
    prayerData = {
      'startYear': 2022,
      'endYear': 2023,
      'percentageMissed': 50,
      'taqwaBuffer': 5,
      'yearlyData': {},
      'monthlyData': {}
    };

    for (int year = prayerData['startYear'];
        year <= prayerData['endYear'];
        year++) {
      prayerData['yearlyData'][year.toString()] = {
        for (var prayer in prayers)
          prayer: {
            'percentMissed': prayerData['percentageMissed'],
            'prayersCompleted': _getTotalPrayersForYear(year) -
                (_getTotalPrayersForYear(year) *
                        prayerData['percentageMissed'] /
                        100)
                    .round(),
            'prayersMissed': (_getTotalPrayersForYear(year) *
                    prayerData['percentageMissed'] /
                    100)
                .round(),
          }
      };
    }
  }

  int getTotalCompletedPrayers() {
    int totalCompleted = 0;
    for (var yearData in prayerData['yearlyData'].values) {
      for (var prayerData in yearData.values) {
        totalCompleted += (prayerData['prayersCompleted'] as num).toInt();
      }
    }
    return totalCompleted;
  }

  Future<bool> resetApplication() async {
    prayerData = {};
    _setDefaultPrayerData();
    await _hiveService.clearData();
    return true;
  }

  void sharePrayerData() {
    String shareText = 'My Prayer Progress:\n\n';
    shareText += 'Total Completed: ${getTotalCompletedPrayers()}\n';
    Share.share(shareText);
  }

  int _getTotalPrayersForYear(int year) {
    return _isLeapYear(year) ? 366 : 365;
  }

  bool _isLeapYear(int year) {
    return year % 4 == 0 && (year % 100 != 0 || year % 400 == 0);
  }
}



