import '../services/prayer_service.dart';

class PrayerProvider with ChangeNotifier {
  final PrayerService _prayerService;
  final monthsList = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December'
  ];

  PrayerProvider(this._prayerService);

  // Getters for accessing data
  int get startYear => _prayerService.prayerData['startYear'];
  int get endYear => _prayerService.prayerData['endYear'];
  int get percentageMissed => _prayerService.prayerData['percentageMissed'];
  int get taqwaBuffer => _prayerService.prayerData['taqwaBuffer'];
  bool get isDataLoaded => _prayerService.isDataLoaded;
  List<String> get prayers => _prayerService.prayers;

  // Methods for updating state and persisting changes
  Future<void> loadData() async {
    await _prayerService.loadData();
    notifyListeners();
  }

  Future<void> saveData(
      int startYear, int endYear, Map<int, int> percentageMissedByYear) async {
    await _prayerService.createDetailedBreakdown(
        startYear, endYear, percentageMissedByYear);
    notifyListeners();
  }

  Map<String, dynamic> getPrayerData() {
    return _prayerService.prayerData;
  }

  Future<void> savePrayerData() async {
    await _prayerService.savePrayerData();
    notifyListeners();
  }

  int getTotalPrayersPossible() {
    return _prayerService.getTotalPrayersPossible(startYear, endYear);
  }

  int getTotalPrayersForPrayerType(String prayer) {
    return _prayerService.getTotalPrayersForPrayerType(prayer);
  }

  Future<void> incrementPrayer(String prayer) async {
    await _prayerService.incrementPrayer(prayer);
    notifyListeners();
  }

  Future<void> updatePrayerData(
      int year, Map<String, dynamic> yearlyData) async {
    await _prayerService.updatePrayerData(year, yearlyData);
    notifyListeners();
  }

  void applyPresetOption(Map<int, int> percentageMissedByYear) {
    var prayerData = _prayerService.prayerData;
    prayerData['yearlyData'] = {};
    for (int year = startYear; year <= endYear; year++) {
      prayerData['yearlyData'][year.toString()] = {
        for (var prayer in prayers)
          prayer: {
            'percentMissed': percentageMissedByYear[year]!,
            'prayersCompleted': _getTotalPrayersForYear(year) -
                (_getTotalPrayersForYear(year) *
                        percentageMissedByYear[year]! /
                        100)
                    .round(),
            'prayersMissed': (_getTotalPrayersForYear(year) *
                    percentageMissedByYear[year]! /
                    100)
                .round(),
          }
      };
    }
    notifyListeners();
  }

  int getTotalCompletedPrayers() {
    return _prayerService.getTotalCompletedPrayers();
  }

  int getCompletedPrayersForPrayerType(String prayer) {
    return _prayerService.getCompletedPrayersForPrayerType(prayer);
  }

  Future<bool> resetApplication() async {
    bool result = await _prayerService.resetApplication();
    if (result) {
      notifyListeners();
    }
    return result;
  }

  void sharePrayerData() {
    _prayerService.sharePrayerData();
  }

  int _getTotalPrayersForYear(int year) {
    return _isLeapYear(year) ? 366 : 365;
  }

  bool _isLeapYear(int year) {
    return year % 4 == 0 && (year % 100 != 0 || year % 400 == 0);
  }

  // New methods for streak, EPPD, and completion time
  int getStreak() {
    // Implement streak calculation logic
