import '../screens/prayer_progress_card_material.dart';
import 'prayer_progress_card_cupertino.dart';
import 'year_selection_screen.dart';
import 'edit_details_screen.dart';
import 'package:charts_flutter/flutter.dart' as charts;

class HomeScreen extends StatelessWidget {
  const HomeScreen({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Consumer<PrayerProvider>(
      builder: (context, prayerProvider, _) {
        if (!prayerProvider.isDataLoaded) {
          prayerProvider.loadData();
          return Platform.isIOS
              ? cupertino.CupertinoPageScaffold(
                  child: Center(child: cupertino.CupertinoActivityIndicator()))
              : Scaffold(body: Center(child: CircularProgressIndicator()));
        }

        int totalPrayersPossible = prayerProvider.getTotalPrayersPossible();
        int totalCompletedPrayers = prayerProvider.getTotalCompletedPrayers();

        Widget body = Padding(
          padding: REdgeInsets.all(16.0),
          child: Column(
            crossAxisAlignment:
                kIsWeb ? CrossAxisAlignment.center : CrossAxisAlignment.stretch,
            children: [
              Text(
                'Total Prayers',
                style: kIsWeb
                    ? TextStyle(
                        fontSize: 18,
                        fontWeight: FontWeight.bold,
                        color: Colors.black)
                    : Platform.isIOS
                        ? TextStyle(
                            fontSize: 18,
                            fontWeight: FontWeight.bold,
                            color: cupertino.CupertinoColors.black)
                        : TextStyle(
                            fontSize: 18,
                            fontWeight: FontWeight.bold,
                            color: Colors.black),
              ),
              SizedBox(height: 8.h),
              Row(
                mainAxisAlignment: MainAxisAlignment.spaceBetween,
                children: [
                  Text(
                    '$totalCompletedPrayers',
                    style: kIsWeb
                        ? TextStyle(
                            fontSize: 18,
                            fontWeight: FontWeight.bold,
                            color: Colors.black)
                        : Platform.isIOS
                            ? TextStyle(
                                fontSize: 18,
                                fontWeight: FontWeight.bold,
                                color: cupertino.CupertinoColors.black)
                            : TextStyle(
                                fontSize: 18,
                                fontWeight: FontWeight.bold,
                                color: Colors.black),
                  ),
                  Text(
                    '/ $totalPrayersPossible',
                    style: kIsWeb
                        ? TextStyle(
                            fontSize: 18,
                            fontWeight: FontWeight.bold,
                            color: Colors.black)
                        : Platform.isIOS
                            ? TextStyle(
                                fontSize: 18,
                                fontWeight: FontWeight.bold,
                                color: cupertino.CupertinoColors.black)
                            : TextStyle(
                                fontSize: 18,
                                fontWeight: FontWeight.bold,
                                color: Colors.black),
                  ),
                ],
              ),
              SizedBox(height: 8.h),
              ClipRRect(
                borderRadius: BorderRadius.circular(8),
                child: LinearProgressIndicator(
                  value: totalPrayersPossible > 0
                      ? totalCompletedPrayers / totalPrayersPossible
                      : 0,
                  minHeight: 16.h,
                  backgroundColor: Colors.grey[300],
                  valueColor: kIsWeb
                      ? AlwaysStoppedAnimation<Color>(Color(0xff34C759))
                      : Platform.isIOS
                          ? AlwaysStoppedAnimation<
                                  cupertino.CupertinoDynamicColor>(
                              cupertino.CupertinoColors.systemGreen)
                          : AlwaysStoppedAnimation<Color>(Color(0xff34C759)),
                ),
              ),
              SizedBox(height: 16.h),
              _buildProgressVisualization(prayerProvider),
              SizedBox(height: 16.h),
              Expanded(
                child: GridView.count(
                  crossAxisCount:
                      kIsWeb && MediaQuery.of(context).size.width > 800 ? 3 : 2,
                  childAspectRatio: kIsWeb ? 1.8.r : 1.1.r,
                  crossAxisSpacing: 12.w,
                  mainAxisSpacing: 12.h,
                  children: prayerProvider.prayers.map((prayer) {
                    return kIsWeb
                        ? PrayerProgressCardMaterial(prayer: prayer)
                        : Platform.isIOS
                            ? PrayerProgressCardCupertino(prayer: prayer)
                            : PrayerProgressCardMaterial(prayer: prayer);
                  }).toList(),
                ),
              ),
              SizedBox(height: 16.h),
              kIsWeb
                  ? AmanaButton(
                      onPress: () async {
                        if (await prayerProvider.resetApplication()) {
                          Navigator.push(
                            context,
                            MaterialPageRoute(
                              builder: (context) => const YearSelectionScreen(),
                            ),
                          );
                        }
                      },
                      label: 'Start Over',
                      isAppLevel: true,
                    )
                  : AmanaButton(
                      onPress: () async {
                        if (await prayerProvider.resetApplication()) {
                          Navigator.push(
                            context,
                            MaterialPageRoute(
                              builder: (context) => const YearSelectionScreen(),
                            ),
                          );
                        }
                      },
                      label: 'Start Over',
                      isAppLevel: true,
                    ),
              Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  kIsWeb
                      ? AmanaButton(
                          onPress: prayerProvider.sharePrayerData,
                          label: 'Share',
                          isAppLevel: true,
                        )
                      : Expanded(
                          child: AmanaButton(
                          onPress: prayerProvider.sharePrayerData,
                          label: 'Share',
                          isAppLevel: true,
                        )),
                  const SizedBox(
                    width: 8,
                  ),
                  kIsWeb
                      ? AmanaButton(
                          onPress: () {
                            Navigator.push(
                              context,
                              MaterialPageRoute(
                                builder: (context) => const EditDetailsScreen(),
                              ),
                            );
                          },
                          label: 'Edit Details',
                          isAppLevel: true,
                        )
                      : Expanded(
                          child: AmanaButton(
                          onPress: () {
                            Navigator.push(
                              context,
                              MaterialPageRoute(
                                builder: (context) => const EditDetailsScreen(),
                              ),
                            );
                          },
                          label: 'Edit Details',
                          isAppLevel: true,
                        )),
                ],
              ),
            ],
          ),
        );

        return AmanaScaffold(title: "Amana Audit", body: body);
      },
    );
  }

  Widget _buildProgressVisualization(PrayerProvider prayerProvider) {
    List<charts.Series<PrayerData, String>> seriesList = [];

    for (var prayer in prayerProvider.prayers) {
      int totalPrayers = prayerProvider.getTotalPrayersForPrayerType(prayer);
      int completedPrayers =
          prayerProvider.getCompletedPrayersForPrayerType(prayer);

      seriesList.add(
        charts.Series<PrayerData, String>(
          id: prayer,
          domainFn: (PrayerData data, _) => data.prayer,
          measureFn: (PrayerData data, _) => data.count,
          data: [
            PrayerData(prayer, completedPrayers),
            PrayerData(prayer, totalPrayers - completedPrayers),
          ],
        ),
      );
    }

    return SizedBox(
      height: 200.h,
      child: charts.BarChart(
        seriesList,
        animate: true,
        barGroupingType: charts.BarGroupingType.grouped,
        defaultRenderer: charts.BarRendererConfig(
          groupingType: charts.BarGroupingType.grouped,
          strokeWidthPx: 1.0,
        ),
        primaryMeasureAxis: charts.NumericAxisSpec(
          tickProviderSpec: charts.BasicNumericTickProviderSpec(
            desiredTickCount: 5,
          ),
        ),
      ),
    );
  }
}

class PrayerData {
  final String prayer;
  final int count;

  PrayerData(this.prayer, this.count);
}



