import 'package:fl_chart/fl_chart.dart';

class HomeScreen extends StatelessWidget {
  const HomeScreen({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Consumer<PrayerProvider>(
      builder: (context, prayerProvider, _) {
        if (!prayerProvider.isDataLoaded) {
          prayerProvider.loadData();
          return Platform.isIOS
              ? cupertino.CupertinoPageScaffold(
                  child: Center(child: cupertino.CupertinoActivityIndicator()))
              : Scaffold(body: Center(child: CircularProgressIndicator()));
        }

        if (prayerProvider.getPrayerData()['yearlyData'] == null) {
          prayerProvider.initializeDefaultPrayerData();
          return Platform.isIOS
              ? cupertino.CupertinoPageScaffold(
                  child: Center(
                    child: Text('Prayer data is being initialized...'),
                  ),
                )
              : Scaffold(
                  body: Center(
                    child: Text('Prayer data is being initialized...'),
                  ),
                );
        }

        int totalPrayersPossible = prayerProvider.getTotalPrayersPossible();
        int totalCompletedPrayers = prayerProvider.getTotalCompletedPrayers();

        Widget body = Padding(
          padding: REdgeInsets.all(16.0),
