    setState(() {
      _tempStartYear = Provider.of<PrayerProvider>(context, listen: false).startYear;
      _tempEndYear = Provider.of<PrayerProvider>(context, listen: false).endYear;
      _startYearController.text = _tempStartYear.toString();
      _endYearController.text = _tempEndYear.toString();
