//uploadFile.js
function uploadFile(formData, callback) {
    const CHUNK_SIZE = 1024 * 1024; // 1MB
    const file = formData.get('image');
    const metadata = formData.get('metadata');
    const audio = formData.get('audio');
    const penData = formData.get('pendata');

    const totalChunks = Math.ceil(file.size / CHUNK_SIZE);
    let currentChunk = 0;

    function uploadChunk(start) {
        const chunk = file.slice(start, start + CHUNK_SIZE);
        const chunkFormData = new FormData();
        chunkFormData.append('chunk', chunk);
        chunkFormData.append('metadata', metadata);
        chunkFormData.append('pendata', penData);
        if (audio) {
            chunkFormData.append('audio', audio);
        }

        fetch(`uploadmay.php?chunk=${currentChunk}&total=${totalChunks}`, {
            method: 'POST',
            body: chunkFormData
        })
        .then(response => response.json())
        .then(data => {
            console.log(data);
            currentChunk++;
            const percentLoaded = Math.ceil((currentChunk / totalChunks) * 100);
            const progressText = document.getElementById('progress-text1');
            const progressBar = document.getElementById('progress1');
            if (progressText && progressBar) {
                progressText.textContent = percentLoaded + '%';
                progressBar.value = percentLoaded;
            }
            if (currentChunk < totalChunks) {
                uploadChunk(currentChunk * CHUNK_SIZE);
            } else {
                console.log('Upload complete');
                document.getElementById('upload-status-text').textContent = "Upload complete!";
                if (callback) {
                    callback();
                }
            }
        })
        .catch(error => {
            console.error('Upload failed:', error);
            document.getElementById('upload-status-text').textContent = "Upload failed!";
        });
    }

    uploadChunk(0);
}//saveData.js
function saveData() {
    if (mediaRecorder.state !== 'inactive') {
        mediaRecorder.onstop = function() {
            console.log("MediaRecorder stopped and data available.");
            finalizeSaveData(() => {
                console.log("Callback: finalizeSaveData complete.");
            });
        };
        mediaRecorder.stop();
    } else {
        finalizeSaveData(() => {
            console.log("Callback: finalizeSaveData complete.");
        });
    }
}

function finalizeSaveData(callback) {
    removeImage();
    const canvasDataUrl = canvas.toDataURL('image/png');
    if (!canvasDataUrl) {
        console.error("Error generating data URL from canvas");
        return;
    }

    const metadata = {
        username: window.username,
        photoURL: document.getElementById('photoURL').value,
        recordId: getQueryParameter('recordId'),
        browser: navigator.userAgent,
        ip: 'N/A', // IP address would need to be fetched from the server
        startTime: new Date(startOffset).toISOString(),
        endTime: new Date().toISOString()
    };

    const metadataBlob = new Blob([JSON.stringify(metadata)], { type: 'application/json' });
    const pointsBlob = new Blob([JSON.stringify(points)], { type: 'application/json' });

    const formData = new FormData();
    formData.append('image', dataUrlToFile(canvasDataUrl, 'drawing.png'));
    formData.append('metadata', metadataBlob);
    formData.append('pendata', pointsBlob);

    if (audioChunks.length > 0) {
        const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });
        formData.append('audio', audioBlob);
        console.log("Audio Blob created:", audioBlob);
    } else {
        console.log("No audio data to upload");
    }

    uploadFile(formData, callback);
}

function dataUrlToFile(dataUrl, filename) {
    const arr = dataUrl.split(',');
    if (arr.length < 2) {
        console.error("Invalid data URL");
        return null;
    }
    const mime = arr[0].match(/:(.*?);/)[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    while (n--) {
        u8arr[n] = bstr.charCodeAt(n);
    }
    return new File([u8arr], filename, { type: mime });
}

function removeImage() {
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    points.forEach((point, index) => {
        if (index > 0) {
            drawLine(points[index - 1], point);
        }
    });
}
