<?php
$targetBaseDir = __DIR__ . "/uploads";

if ($_SERVER["REQUEST_METHOD"] == "POST") {
    $timestamp = date("YmdHis");
    $sessionDir = "/uploads/" . $timestamp; // Relative path for the public URL

    if (!file_exists(__DIR__ . $sessionDir)) {
        mkdir(__DIR__ . $sessionDir, 0777, true); // Create the directory if it doesn't exist
    }

    $chunkNumber = isset($_GET['chunk']) ? intval($_GET['chunk']) : 0;
    $totalChunks = isset($_GET['total']) ? intval($_GET['total']) : 1;

    $fileName = __DIR__ . $sessionDir . "/drawing_" . $timestamp . "_" . $chunkNumber . ".png";
    if (move_uploaded_file($_FILES["chunk"]["tmp_name"], $fileName)) {
        // Save metadata and audio files if available
        if ($chunkNumber === 0) {
            if (isset($_FILES["metadata"])) {
                $metadataFileName = __DIR__ . $sessionDir . "/metadata_" . $timestamp . ".json";
                move_uploaded_file($_FILES["metadata"]["tmp_name"], $metadataFileName);
            }
            if (isset($_FILES["pendata"])) {
                $penDataFileName = __DIR__ . $sessionDir . "/pendata_" . $timestamp . ".json";
                move_uploaded_file($_FILES["pendata"]["tmp_name"], $penDataFileName);
            }
            if (isset($_FILES["audio"])) {
                $audioFileName = __DIR__ . $sessionDir . "/audio_" . $timestamp . ".webm";
                move_uploaded_file($_FILES["audio"]["tmp_name"], $audioFileName);
            }
        }

        if ($chunkNumber === $totalChunks - 1) {
            // Combine all chunks into a single file
            $finalFileName = __DIR__ . $sessionDir . "/drawing_" . $timestamp . ".png";
            $finalFile = fopen($finalFileName, "w");
            for ($i = 0; $i < $totalChunks; $i++) {
                $chunkFileName = __DIR__ . $sessionDir . "/drawing_" . $timestamp . "_" . $i . ".png";
                $chunkData = file_get_contents($chunkFileName);
                fwrite($finalFile, $chunkData);
                unlink($chunkFileName); // Clean up chunk files
            }
            fclose($finalFile);
        }

        echo json_encode(["success" => true, "sessionDir" => $sessionDir, "timestamp" => $timestamp]);
    } else {
        echo json_encode(["success" => false, "error" => "Failed to move uploaded file."]);
    }
} else {
    echo json_encode(["success" => false, "error" => "Invalid request method."]);
}
?>
