class MyApp extends StatelessWidget {
  final PrayerProvider prayerProvider;

  const MyApp({super.key, required this.prayerProvider});

  @override
  Widget build(BuildContext context) {
    return ChangeNotifierProvider.value(
      value: prayerProvider,
      child: AmanaMaterialApp(
        title: 'Amana Audit',
        debugShowCheckedModeBanner: false,
        theme: ThemeData(
            fontFamily: "OpenSans",
            scaffoldBackgroundColor: Colors.grey[100],
            primarySwatch: Colors.blue),
        initialRoute: '/',
        routes: {
          '/': (context) => const HomeScreen(),
          '/year-selection': (context) => const YearSelectionScreen(),
          '/edit-details': (context) => const EditDetailsScreen()
        },
      ),
    );
  }
}



