import 'package:flutter/foundation.dart';
import '../services/prayer_service.dart';

class PrayerProvider with ChangeNotifier {
  final PrayerService _prayerService;
  final monthsList = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December'
  ];

  Map<String, dynamic> _prayerData = {};

  Map<String, dynamic> get prayerData => _prayerData;

  set prayerData(Map<String, dynamic> value) {
    _prayerData = value;
    notifyListeners();
  }

  PrayerProvider(this._prayerService) {
    _setDefaultPrayerData();
  }

  int get birthYear => prayerData['birthYear'] ?? 2000;
  int get startYear => prayerData['startYear'] ?? DateTime.now().year;
  int get startMonth => prayerData['startMonth'] ?? 1;
  int get endYear => prayerData['endYear'] ?? DateTime.now().year;
  int get endMonth => prayerData['endMonth'] ?? DateTime.now().month;
  int get percentageMissed => prayerData['percentageMissed'] ?? 25;
  int get taqwaBuffer => prayerData['taqwaBuffer'] ?? 5;
  bool get isDataLoaded => _prayerService.isDataLoaded;
  List<String> get prayers => _prayerService.prayers;

  Future<void> loadData() async {
    await _prayerService.loadData();
    prayerData = _prayerService.prayerData;
    notifyListeners();
  }

  Future<void> saveData(int startYear, int startMonth, int endYear,
      int endMonth, Map<int, int> percentageMissedByYear) async {
    prayerData['startYear'] = startYear;
    prayerData['startMonth'] = startMonth;
    prayerData['endYear'] = endYear;
    prayerData['endMonth'] = endMonth;

    prayerData['months'] = [
      for (int year = startYear; year <= endYear; year++)
        for (int month = (year == startYear ? startMonth : 1);
            month <= (year == endYear ? endMonth : 12);
            month++)
          {'year': year, 'month': month}
    ];

    prayerData['yearlyData'] = {
      for (int year = startYear; year <= endYear; year++)
        year.toString(): {
          for (var prayer in prayers)
            prayer: {
              'percentMissed': percentageMissedByYear[year]!,
              'prayersCompleted': getTotalPrayersForYear(year) -
                  (getTotalPrayersForYear(year) *
                          percentageMissedByYear[year]! /
                          100)
                      .round(),
              'prayersMissed': (getTotalPrayersForYear(year) *
                      percentageMissedByYear[year]! /
                      100)
                  .round(),
              'monthlyData': _getMonthlyDataForYear(
                  year, prayer, percentageMissedByYear[year]!),
            }
        }
    };

    await savePrayerData();
    notifyListeners();
  }

  List<Map<String, int>> _getMonthlyDataForYear(
      int year, String prayer, int percentMissed) {
    List<Map<String, int>> monthlyData = [];
    for (int month = 1; month <= 12; month++) {
      int daysInMonth = DateTime(year, month + 1, 0).day;
      int totalPrayers = daysInMonth;
      int prayersMissed = (totalPrayers * percentMissed / 100).round();
      int prayersCompleted = totalPrayers - prayersMissed;
      monthlyData.add({
        'month': month,
        'totalPrayers': totalPrayers,
        'prayersCompleted': prayersCompleted,
        'prayersMissed': prayersMissed,
      });
    }
    return monthlyData;
  }

  Map<String, dynamic> getPrayerData() {
    return _prayerService.prayerData;
  }

  Future<void> savePrayerData() async {
    await _prayerService.savePrayerData();
    notifyListeners();
  }

  int getTotalPrayersPossible() {
    int totalPossible = 0;
    for (var year in prayerData['yearlyData'].keys) {
      totalPossible += _calculateYearlyTotalPossible(int.parse(year));
    }
    return totalPossible;
  }

  int getTotalPrayersMissed() {
    int totalMissed = 0;
    for (var yearData in prayerData['yearlyData'].values) {
      for (var prayerData in yearData.values) {
        totalMissed += (prayerData['prayersMissed'] as num).toInt();
      }
    }
    return totalMissed;
  }

  int getTotalPrayersForPrayerType(String prayer) {
    int totalPrayers = 0;
    for (var year = startYear; year <= endYear; year++) {
      totalPrayers += _calculateYearlyTotalPossible(year);
    }
    return totalPrayers;
  }

  int getTotalMissedPrayersForPrayerType(String prayer) {
    int totalMissed = 0;
    for (var yearData in prayerData['yearlyData'].values) {
      if (yearData[prayer] != null) {
        totalMissed += (yearData[prayer]['prayersMissed'] as num).toInt();
      }
    }
    return totalMissed;
  }

  Future<void> incrementPrayer(String prayer) async {
    await _prayerService.incrementPrayer(prayer);
    notifyListeners();
  }

  int _calculateYearlyTotalPossible(int year) {
    int totalPossible = 0;
    if (prayerData['yearlyData'][year.toString()] != null) {
      for (var prayer in prayers) {
        totalPossible += (prayerData['yearlyData'][year.toString()][prayer]
                    ['prayersMissed'] as num)
                .toInt() +
            (prayerData['yearlyData'][year.toString()][prayer]
                    ['prayersCompleted'] as num)
                .toInt();
      }
    }
    return totalPossible;
  }

  void updatePercentageMissed(int year, int percentage) {
    prayerData['yearlyData'][year.toString()].forEach((prayerData) {
      prayerData['percentageMissed'] = percentage;
      prayerData['prayersMissed'] = (365 * 5 * percentage / 100).round();
      prayerData['prayersCompleted'] = (365 * 5) - prayerData['prayersMissed'];
    });
    notifyListeners();
  }

  Future<void> updatePrayerData(String prayer, int year, int prayersCompleted,
      int prayersMissed, int percentageMissed) async {
    await _prayerService.updatePrayerData(
        prayer, year, prayersCompleted, prayersMissed, percentageMissed);
    notifyListeners();
  }

  void _setDefaultPrayerData() {
    final currentYear = DateTime.now().year;
    prayerData = {
      'metadata': {
        'createdAt': DateTime.now().toIso8601String(),
        'updatedAt': DateTime.now().toIso8601String(),
        'dataSource': 'Default',
      },
      'prayerData': [],
      'birthYear': 2000,
      'startYear': currentYear,
      'startMonth': 1,
      'endYear': currentYear,
      'endMonth': DateTime.now().month,
      'yearlyData': {
        currentYear.toString(): {
          for (var prayer in prayers)
            prayer: {
              'percentMissed': 50,
              'prayersCompleted': 0,
              'prayersMissed': getTotalPrayersForYear(currentYear),
              'totalPossible': 0,
            }
        }
      }
    };
  }

  Future<void> initializeDefaultPrayerData() async {
    _setDefaultPrayerData();
    await savePrayerData();
  }

  void applyPresetOption(Map<int, int> percentageMissedByYear) {
    var prayerData = _prayerService.prayerData;
    prayerData['yearlyData'] = {};
    for (int year = startYear; year <= endYear; year++) {
      prayerData['yearlyData'][year.toString()] = {
        for (var prayer in prayers)
          prayer: {
            'percentMissed': percentageMissedByYear[year]!,
            'prayersCompleted': getTotalPrayersForYear(year) -
                (getTotalPrayersForYear(year) *
                        percentageMissedByYear[year]! /
                        100)
                    .round(),
            'prayersMissed': (getTotalPrayersForYear(year) *
                    percentageMissedByYear[year]! /
                    100)
                .round(),
          }
      };
    }
    notifyListeners();
  }

  int getTotalPrayersForYear(int year) {
    return _prayerService.getTotalPrayersForYear(year);
  }

  int getTotalCompletedPrayers() {
    int totalCompleted = 0;
    for (var yearData in prayerData['yearlyData'].values) {
      for (var prayerData in yearData.values) {
        totalCompleted += (prayerData['prayersCompleted'] as num).toInt();
      }
    }
    return totalCompleted;
  }

  int getCompletedPrayersForPrayerType(String prayer) {
    return _prayerService.getCompletedPrayersForPrayerType(prayer);
  }

  Future<bool> resetApplication() async {
    bool result = await _prayerService.resetApplication();
    if (result) {
      notifyListeners();
    }
    return result;
  }

  void sharePrayerData() {
    _prayerService.sharePrayerData();
  }

  int getStreak() {
    return 0; // Placeholder
  }

  double getEPPD() {
    return 0.0; // Placeholder
  }

  int getCompletionTime() {
    return 0; // Placeholder
  }

  Future<void> saveBirthYear(int birthYear) async {
    await _prayerService.saveBirthYear(birthYear);
    notifyListeners();
  }

  int _calculateTotalPossiblePrayers(int startYear, int endYear) {
    int totalPossible = 0;
    for (var year in prayerData['yearlyData'].keys) {
      totalPossible += _calculateYearlyTotalPossible(int.parse(year));
    }
    return totalPossible;
  }

  int _calculateYearlyTotalPossible(int year) {
    int totalPossible = 0;
    if (prayerData['yearlyData'][year.toString()] != null) {
      for (var prayer in prayers) {
        totalPossible += (prayerData['yearlyData'][year.toString()][prayer]
                    ['prayersMissed'] as num)
                .toInt() +
            (prayerData['yearlyData'][year.toString()][prayer]
                    ['prayersCompleted'] as num)
                .toInt();
      }
    }
    return totalPossible;
  }
}



