import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import '../platform_specific_widget/amana_scaffold.dart';
import '../platform_specific_widget/amana_input_field.dart';
import '../providers/prayer_provider.dart';

class SettingsScreen extends StatefulWidget {
  @override
  _SettingsScreenState createState() => _SettingsScreenState();
}

class _SettingsScreenState extends State<SettingsScreen> {
  late TextEditingController _birthYearController;

  @override
  void initState() {
    super.initState();
    final prayerProvider = Provider.of<PrayerProvider>(context, listen: false);
    _birthYearController =
        TextEditingController(text: prayerProvider.birthYear.toString());
  }

  @override
  void dispose() {
    _birthYearController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return AmanaScaffold(
      title: 'Settings',
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.stretch,
          children: [
            AmanaInputField(
              keyboardType: TextInputType.number,
              controller: _birthYearController,
              onChange: (val) {
                setState(() {
                  _birthYearController.text = val;
                });
              },
              sufixIcon: Icons.date_range,
              label: "Birth Year",
            ),
            SizedBox(height: 16),
            ElevatedButton(
              onPressed: () {
                int birthYear = int.parse(_birthYearController.text);
                Provider.of<PrayerProvider>(context, listen: false)
                    .saveBirthYear(birthYear);
                Navigator.pop(context);
              },
              child: Text('Save'),
            ),
          ],
        ),
      ),
    );
  }
}



