//prayer progress card material . dart
import '../platform_specific_widget/amana_button.dart';
import 'package:flutter/material.dart';
import 'package:flutter_screenutil/flutter_screenutil.dart';
import 'package:provider/provider.dart';
import '../providers/prayer_provider.dart';

class PrayerProgressCardMaterial extends StatelessWidget {
  final String prayer;

  const PrayerProgressCardMaterial({required this.prayer});

  @override
  Widget build(BuildContext context) {
    final prayerProvider = Provider.of<PrayerProvider>(context);

    int totalPrayers = prayerProvider.getTotalPrayersForPrayerType(prayer);
    int completedPrayers =
        prayerProvider.getCompletedPrayersForPrayerType(prayer);

    return Card(
      child: Padding(
        padding: REdgeInsets.all(14),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          children: [
            Text(prayer,
                style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16)),
            ClipRRect(
              borderRadius: BorderRadius.circular(8).r,
              child: LinearProgressIndicator(
                value: totalPrayers > 0 ? (completedPrayers / totalPrayers).clamp(0, 1) : 0,
                minHeight: 12.h,
                backgroundColor: Colors.grey[300],
                valueColor:
                    const AlwaysStoppedAnimation<Color>(Color(0xff34C759)),
              ),
            ),
            Text(
                '${(completedPrayers / totalPrayers * 100).toStringAsFixed(1)}%',
                style: TextStyle(fontSize: 14)),
            Text('$completedPrayers / $totalPrayers',
                style: TextStyle(fontSize: 12)),
            AmanaButton(
              onPress: () => prayerProvider.incrementPrayer(prayer),
              label: 'Pray',
              isAppLevel: false,
            ),
          ],
        ),
      ),
    );
  }
}



