import 'dart:io';
import '../constants.dart';
import '../widgets/hadith_modal.dart';
import 'package:flutter/cupertino.dart';
import 'package:flutter/foundation.dart';
import 'package:flutter/material.dart';
import 'package:flutter_screenutil/flutter_screenutil.dart';
import 'package:provider/provider.dart';
import '../platform_specific_widget/amana_button.dart';
import '../platform_specific_widget/amana_input_field.dart';
import '../providers/prayer_provider.dart';
import 'home_screen.dart';

class YearSelectionScreen extends StatefulWidget {
  const YearSelectionScreen({super.key});

  @override
  State<YearSelectionScreen> createState() => _YearSelectionScreenState();
}

class _YearSelectionScreenState extends State<YearSelectionScreen> {
  late TextEditingController _startYearController;
  late TextEditingController _startMonthController;
  late TextEditingController _endYearController;
  late TextEditingController _endMonthController;
  double _averageMissedPrayers = 25;

  @override
  void initState() {
    super.initState();
    final provider = Provider.of<PrayerProvider>(context, listen: false);
    _startYearController =
        TextEditingController(text: provider.startYear.toString());
    _startMonthController = TextEditingController(text: '1');
    _endYearController =
        TextEditingController(text: provider.endYear.toString());
    _endMonthController =
        TextEditingController(text: DateTime.now().month.toString());
  }

  @override
  void dispose() {
    _startYearController.dispose();
    _startMonthController.dispose();
    _endYearController.dispose();
    _endMonthController.dispose();
    super.dispose();
  }

  void _showHadithModal(BuildContext context) {
    Platform.isIOS
        ? showCupertinoDialog(
            context: context,
            builder: (BuildContext context) {
              return HadithModal();
            },
          )
        : showDialog(
            context: context,
            builder: (BuildContext context) {
              return HadithModal();
            },
          );
  }

  void _saveData() {
    int startYear =
        int.tryParse(_startYearController.text) ?? kDefaultStartYear;
    int startMonth = int.tryParse(_startMonthController.text) ?? 1;
    int endYear = int.tryParse(_endYearController.text) ?? kDefaultEndYear;
    int endMonth =
