import 'hive_service.dart';
import 'package:share_plus/share_plus.dart';

class PrayerService {
  final HiveService _hiveService;
  bool isDataLoaded = false;
  final List<String> prayers = ['Fajr', 'Dhuhr', 'Asr', 'Maghrib', 'Isha'];
  Map<String, dynamic> prayerData = {};

  PrayerService(this._hiveService);

  Future<void> loadData() async {
    var loadedData = await _hiveService.loadData('prayerData');

    if (loadedData != null && loadedData is Map) {
      prayerData =
          Map<String, dynamic>.from(loadedData.cast<String, dynamic>());
    } else {
      _setDefaultPrayerData();
      savePrayerData();
    }

    isDataLoaded = true;
    print('Loaded data from Hive: $prayerData');
  }

  Future<void> createDetailedBreakdown(
      int startYear, int endYear, Map<int, int> percentageMissedByYear) async {
    prayerData['prayerData'] = [
      for (int year = startYear; year <= endYear; year++)
        {
          'year': year,
          'totalPrayed': 0,
          'totalMissed': 0,
          'months': [
            for (int month = 1; month <= 12; month++)
              {
                'monthNumber': month,
                'totalPrayed': 0,
                'totalMissed': 0,
                'weeks': [
                  for (int week = 1; week <= 5; week++)
                    {
                      'weekNumber': week,
                      'totalPrayed': 0,
                      'totalMissed': 0,
                      'days': [
                        for (int day = 1; day <= 7; day++)
                          {
                            'dayNumber': day,
                            'prayers': [
                              for (var prayer in prayers)
                                {'prayerName': prayer, 'missed': false}
                            ],
                          },
                      ],
                    },
                ],
              },
          ],
        },
    ];
    await savePrayerData();
  }

  Future<void> updatePrayerData(String prayer, int year, int prayersCompleted,
      int prayersMissed, int percentageMissed) async {
    if (prayerData['yearlyData'][year.toString()] == null) {
      prayerData['yearlyData'][year.toString()] = {};
    }
    prayerData['yearlyData'][year.toString()][prayer] = {
      'prayersCompleted': prayersCompleted,
      'prayersMissed': prayersMissed,
      'percentMissed': percentageMissed,
    };
    await savePrayerData();
  }

  int getTotalPrayersPossible(int startYear, int endYear) {
    int totalPossible = 0;
    for (int year = startYear; year <= endYear; year++) {
      totalPossible +=
          isLeapYear(year) ? 366 * prayers.length : 365 * prayers.length;
    }
    return totalPossible;
  }

  int getCompletedPrayersForPrayerType(String prayer) {
    int completedPrayers = 0;
    for (var year = prayerData['startYear'];
        year <= prayerData['endYear'];
        year++) {
      if (prayerData['yearlyData'][year.toString()] != null &&
          prayerData['yearlyData'][year.toString()][prayer] != null) {
        completedPrayers += prayerData['yearlyData'][year.toString()][prayer]
            ['prayersCompleted'] as int;
      }
    }
    return completedPrayers;
  }

  int getTotalPrayersForYear(int year) {
    return isLeapYear(year) ? 366 * prayers.length : 365 * prayers.length;
  }

  int getTotalPrayersForPrayerTypeInYear(String prayer, int year) {
    int totalPrayers = 0;
    if (prayerData['yearlyData'][year.toString()] != null &&
        prayerData['yearlyData'][year.toString()][prayer] != null) {
      totalPrayers = prayerData['yearlyData'][year.toString()][prayer]
                  ['prayersCompleted'] +
              prayerData['yearlyData'][year.toString()][prayer]['prayersMissed']
          as int;
    }
    return totalPrayers;
  }

  Future<void> incrementPrayer(String prayer) async {
    for (int year = prayerData['startYear'];
        year <= prayerData['endYear'];
        year++) {
      var prayerDataForYear = prayerData['yearlyData'][year.toString()][prayer];
      if (prayerDataForYear == null) {
        prayerDataForYear = {
          'prayersCompleted': 0,
          'prayersMissed': getTotalPrayersForYear(year),
          'percentMissed': 100,
        };
      }
      int totalPrayersForYear = getTotalPrayersForYear(year);
      if (prayerDataForYear['prayersCompleted'] < totalPrayersForYear) {
        prayerDataForYear['prayersCompleted']++;
        prayerDataForYear['prayersMissed'] =
            totalPrayersForYear - prayerDataForYear['prayersCompleted'];
        prayerDataForYear['percentMissed'] =
            (prayerDataForYear['prayersMissed'] / totalPrayersForYear * 100)
                .round();

        await savePrayerData();
        break; // Break after updating the first incomplete year
      }
    }
  }

  Future<void> saveData(int startYear, int startMonth, int endYear,
      int endMonth, Map<int, int> percentageMissedByYear) async {
    prayerData['startYear'] = startYear;
    prayerData['startMonth'] = startMonth;
    prayerData['endYear'] = endYear;
    prayerData['endMonth'] = endMonth;

    prayerData['months'] = [
      for (int year = startYear; year <= endYear; year++)
        for (int month = (year == startYear ? startMonth : 1);
            month <= (year == endYear ? endMonth : 12);
            month++)
          {'year': year, 'month': month}
    ];

    prayerData['yearlyData'] = {
      for (int year = startYear; year <= endYear; year++)
        year.toString(): {
          for (var prayer in prayers)
            prayer: {
              'percentMissed': percentageMissedByYear[year]!,
              'prayersCompleted': getTotalPrayersForYear(year) -
                  (getTotalPrayersForYear(year) *
                          percentageMissedByYear[year]! /
                          100)
                      .round(),
              'prayersMissed': (getTotalPrayersForYear(year) *
                      percentageMissedByYear[year]! /
                      100)
                  .round(),
            }
        }
    };

    await savePrayerData();
  }

  int getTotalPrayersMissed() {
    int totalMissed = 0;
    for (var yearData in prayerData['yearlyData'].values) {
      for (var prayerData in yearData.values) {
        totalMissed += (prayerData['prayersMissed'] as num).toInt();
      }
    }
    return totalMissed;
  }

  int getTotalMissedPrayersForPrayerType(String prayer) {
    int totalMissed = 0;
    for (var yearData in prayerData['yearlyData'].values) {
      if (yearData[prayer] != null) {
        totalMissed += (yearData[prayer]['prayersMissed'] as num).toInt();
      }
    }
    return totalMissed;
  }

  Future<void> savePrayerData() async {
    await _hiveService.saveData('prayerData', prayerData);
    print('Saved data to Hive: $prayerData');
  }

  void _setDefaultPrayerData() {
    final currentYear = DateTime.now().year;
    prayerData = {
      'metadata': {
        'createdAt': DateTime.now().toIso8601String(),
        'updatedAt': DateTime.now().toIso8601String(),
        'dataSource': 'Default',
      },
      'prayerData': [],
      'birthYear': 2000,
      'startYear': currentYear,
      'endYear': currentYear,
      'yearlyData': {
        currentYear.toString(): {
          for (var prayer in prayers)
            prayer: {
              'percentMissed': 50,
              'prayersCompleted': 0,
              'prayersMissed': getTotalPrayersForYear(currentYear),
              'totalPossible': getTotalPrayersForYear(currentYear),
            }
        }
      }
    };
  }

  int getTotalCompletedPrayers() {
    int totalCompleted = 0;
    for (var yearData in prayerData['yearlyData'].values) {
      for (var prayerData in yearData.values) {
        totalCompleted += (prayerData['prayersCompleted'] as num).toInt();
      }
    }
    return totalCompleted;
  }

  Future<bool> resetApplication() async {
    prayerData = {};
    _setDefaultPrayerData();
    await _hiveService.clearData();
    return true;
  }

  void sharePrayerData() {
    String shareText = 'My Prayer Progress:\n\n';
    shareText += 'Total Completed: ${getTotalCompletedPrayers()}\n';
    Share.share(shareText);
  }

  bool isLeapYear(int year) {
    return year % 4 == 0 && (year % 100 != 0 || year % 400 == 0);
  }

  Future<void> saveBirthYear(int birthYear) async {
    prayerData['birthYear'] = birthYear;
    await savePrayerData();
  }
}



