class PrayerProgressCardMaterial extends StatelessWidget {
  final String prayer;

  const PrayerProgressCardMaterial({required this.prayer});

  @override
  Widget build(BuildContext context) {
    final prayerProvider = Provider.of<PrayerProvider>(context);

    int year = DateTime.now().year;
    int month = DateTime.now().month;
    int completedPrayers = prayerProvider.getCompletedPrayersForMonth(year, month, prayer);
    int missedPrayers = prayerProvider.getMissedPrayersForMonth(year, month, prayer);
    double completionPercentage = prayerProvider.getPrayerCompletionPercentageForMonth(year, month, prayer);

    return Card(
      child: Padding(
        padding: const EdgeInsets.all(14.0),
