class PrayerProgressCardCupertino extends StatelessWidget {
  final String prayer;

  const PrayerProgressCardCupertino({required this.prayer});

  @override
  Widget build(BuildContext context) {
    final prayerProvider = Provider.of<PrayerProvider>(context);

    int totalPrayers = prayerProvider.getTotalPrayersForPrayerType(prayer);
    int completedPrayers = 0;
    final startYear = prayerProvider.startYear;
    final endYear = prayerProvider.endYear;

    for (var year = startYear; year <= endYear; year++) {
      final yearlyData = prayerProvider.getPrayerData()['yearlyData'] ?? {};
      final prayerData = yearlyData[year.toString()] ?? {};
      completedPrayers += (prayerData[prayer]?['prayersCompleted'] ?? 0) as int;
    }

    return CupertinoCard(
      child: Padding(
        padding: REdgeInsets.all(16),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          children: [
            Text(prayer,
                style: const TextStyle(
                    fontWeight: FontWeight.bold, color: CupertinoColors.black)),
            const SizedBox(height: 8.0),
            material.LinearProgressIndicator(
              borderRadius: BorderRadius.circular(15),
              value: totalPrayers > 0 ? (completedPrayers / totalPrayers).clamp(0, 1) : 0,
              minHeight: 10.h,
              backgroundColor: CupertinoColors.systemGrey5,
              valueColor: const AlwaysStoppedAnimation<CupertinoDynamicColor>(
                  CupertinoColors.systemGreen),
            ),
            Text(
                '${(completedPrayers / totalPrayers * 100).toStringAsFixed(1)}%',
                style: TextStyle(fontSize: 14, color: CupertinoColors.black)),
            Text('$completedPrayers / $totalPrayers',
                style: TextStyle(fontSize: 12, color: CupertinoColors.black)),
            AmanaButton(
              onPress: () => prayerProvider.incrementPrayer(prayer),
              isAppLevel: false,
              label: "Pray",
            ),
          ],
        ),
      ),
    );
  }
}

class CupertinoCard extends StatelessWidget {
  final Widget child;

  const CupertinoCard({Key? key, required this.child}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Container(
      decoration: BoxDecoration(
        color: CupertinoColors.systemGrey6,
        borderRadius: BorderRadius.circular(12).r,
        boxShadow: [
          BoxShadow(
            color: CupertinoColors.black.withOpacity(0.1),
            blurRadius: 10,
            offset: const Offset(0, 2),
          ),
        ],
      ),
      child: child,
    );
  }
}



