        int totalPrayersPossible = prayerProvider.getTotalPrayersPossible();
        int totalMissedPrayers = prayerProvider.getTotalPrayersMissed();

        return Scaffold(
          appBar: AppBar(
            title: Text('Amana Audit'),
            actions: [
              IconButton(
                icon: Icon(Icons.edit),
                onPressed: () {
                  Navigator.push(
                    context,
                    MaterialPageRoute(
                        builder: (context) => EditDetailsScreen()),
                  );
                },
              ),
              IconButton(
                icon: Icon(Icons.refresh),
                onPressed: () async {
                  await prayerProvider.resetApplication();
                  prayerProvider.loadData();
                },
              ),
            ],
          ),
          body: Padding(
            padding: const EdgeInsets.all(16.0),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.stretch,
