import 'hive_service.dart';

class PrayerService {
  final HiveService _hiveService;
  bool isDataLoaded = false;
  final List<String> prayers = ['Fajr', 'Dhuhr', 'Asr', 'Maghrib', 'Isha'];
  Map<String, dynamic> prayerData = {};

  PrayerService(this._hiveService);

  Future<void> loadData() async {
    var loadedData = await _hiveService.loadData('prayerData');
    if (loadedData != null && loadedData is Map) {
      prayerData =
          Map<String, dynamic>.from(loadedData.cast<String, dynamic>());
    } else {
      _setDefaultPrayerData();
      savePrayerData();
    }
    isDataLoaded = true;
    print('Loaded data from Hive: $prayerData');
  }

  Future<void> initializeMonthlyPrayerStats(int year, int month) async {
    final firstDayOfMonth = DateTime(year, month, 1);
    final daysInMonth = DateTime(year, month + 1, 0).day;
    final initialCompleted = (daysInMonth / 2).round();
    final initialMissed = daysInMonth - initialCompleted;

    for (var prayer in prayers) {
      final key = '${year}_${month}_$prayer';
      if (!prayerData.containsKey(key)) {
        prayerData[key] = {
          'done': initialCompleted,
          'not_done': initialMissed,
        };
      }
    }
    await savePrayerData();
  }

  Future<void> updatePrayerStats(
      int year, int month, String prayer, bool done) async {
    final key = '${year}_${month}_$prayer';
    if (prayerData.containsKey(key)) {
      if (done) {
        prayerData[key]['done']++;
        prayerData[key]['not_done']--;
      } else {
        prayerData[key]['done']--;
        prayerData[key]['not_done']++;
      }
    }
    await savePrayerData();
  }

  Future<void> savePrayerData() async {
    await _hiveService.saveData('prayerData', prayerData);
    _hiveService.logDatabaseStructure();
  }

  void _setDefaultPrayerData() {
    final currentYear = DateTime.now().year;
    final currentMonth = DateTime.now().month;
    prayerData = {
      'metadata': {
        'createdAt': DateTime.now().toIso8601String(),
        'updatedAt': DateTime.now().toIso8601String(),
        'dataSource': 'Default',
      },
    };
    initializeMonthlyPrayerStats(currentYear, currentMonth);
  }
}



