import 'package:hive/hive.dart';

class HiveService {
  static const String prayerBoxName = 'prayerBox';

  Future<void> init() async {
    await Hive.openBox(prayerBoxName);
  }

  Future<Map<String, dynamic>> loadData(String key) async {
    final box = await Hive.openBox(prayerBoxName);
    return Map<String, dynamic>.from(box.get(key, defaultValue: {}));
  }

  Future<void> saveData(String key, Map<String, dynamic> data) async {
    final box = await Hive.openBox(prayerBoxName);
    await box.put(key, data);
  }

  Future<void> clearData() async {
    final box = await Hive.openBox(prayerBoxName);
    await box.clear();
  }
}
