      ..ishaMissed = fields[6] as int;
  }

  @override
  void write(BinaryWriter writer, PrayerRecord obj) {
    writer
      ..writeByte(7)
      ..writeByte(0)
      ..write(obj.year)
      ..writeByte(1)
      ..write(obj.month)
      ..writeByte(2)
      ..write(obj.fajrMissed)
      ..writeByte(3)
      ..write(obj.dhuhrMissed)
      ..writeByte(4)
      ..write(obj.asrMissed)
      ..writeByte(5)
      ..write(obj.maghribMissed)
      ..writeByte(6)
      ..write(obj.ishaMissed);
  }

  @override
  int get hashCode => typeId.hashCode;

  @override
  bool operator ==(Object other) =>
      identical(this, other) ||
      other is PrayerRecordAdapter &&
          runtimeType == other.runtimeType &&
          typeId == other.typeId;
}



