import 'package:hive/hive.dart';
import '../services/hive_service.dart';

class PrayerService {
  final HiveService _hiveService;

  Map<String, dynamic> prayerData = {};

  PrayerService(this._hiveService);

  Future<void> loadData() async {
    prayerData = await _hiveService.loadData('prayerData');
  }

  Future<void> initializeMonthlyPrayerStats(int year, int month) async {
    final keyPrefix = '${year}_${month}_';
    for (var prayer in ['Fajr', 'Dhuhr', 'Asr', 'Maghrib', 'Isha']) {
      final key = keyPrefix + prayer;
      prayerData[key] = {'done': 15, 'not_done': 15};
    }
    await _hiveService.saveData('prayerData', prayerData);
  }

  Future<void> updatePrayerStats(
      int year, int month, String prayer, bool done) async {
    final key = '${year}_${month}_$prayer';
    if (done) {
      prayerData[key]['done']++;
      prayerData[key]['not_done']--;
    } else {
      prayerData[key]['done']--;
      prayerData[key]['not_done']++;
    }
    await _hiveService.saveData('prayerData', prayerData);
  }

  Future<void> clearData() async {
    prayerData = {};
    await _hiveService.clearData();
  }

  Future<void> savePrayerData() async {
    await _hiveService.saveData('prayerData', prayerData);
  }
}



