    required this.year,
    required this.month,
    required this.prayersCompleted,
    required this.prayersMissed,
    required this.totalPrayed,
    required this.totalMissed,
    required this.percentagePrayed,
    required this.percentageMissed,
  });

  factory PrayerData.fromMap(Map<String, dynamic> map) {
    return PrayerData(
      year: map['year'],
      month: map['month'],
      prayersCompleted: Map<String, int>.from(map['prayersCompleted']),
      prayersMissed: Map<String, int>.from(map['prayersMissed']),
      totalPrayed: map['totalPrayed'],
      totalMissed: map['totalMissed'],
      percentagePrayed: map['percentagePrayed'],
      percentageMissed: map['percentageMissed'],
