  Map<String, dynamic> getPrayerData() {
    return _prayerData;
  }

  void updatePrayerData(
      String prayer, int year, int done, int notDone, int percentage) {
    final key = '${year}_${DateTime.now().month}_$prayer';
    _prayerData[key] = {
      'done': done,
      'not_done': notDone,
      'percentage': percentage,
    };
    notifyListeners();
  }

  void saveBirthYear(int birthYear) {
    _prayerData['birthYear'] = birthYear;
    notifyListeners();
  }
}



