            keyboardType: widget.keyboardType,
            placeholder: widget.hintText,
            prefix: Padding(
              padding: EdgeInsets.all(8.0),
              child: Icon(
                widget.sufixIcon,
                color: Colors.grey,
                size: 20,
              ),
