      builder: (context, prayerProvider, _) {
        if (!prayerProvider.isDataLoaded) {
          prayerProvider.loadData();
          return Scaffold(body: Center(child: CircularProgressIndicator()));
        }

        int totalPrayersPossible = 30 * 5; // Assuming 5 prayers a day
        int totalMissedPrayers = prayerProvider.prayerData.values.fold(
            0,
            (previousValue, element) =>
                previousValue + (element['not_done'] as int));

        return Scaffold(
          appBar: AppBar(
            title: Text('Amana Audit'),
            actions: [
              IconButton(
                icon: Icon(Icons.edit),
                onPressed: () {
                  Navigator.push(
                    context,
                    MaterialPageRoute(
                        builder: (context) => EditDetailsScreen()),
                  );
                },
              ),
              IconButton(
                icon: Icon(Icons.refresh),
                onPressed: () async {
                  await prayerProvider.resetApplication();
                  prayerProvider.loadData();
                },
              ),
            ],
          ),
          body: Padding(
            padding: const EdgeInsets.all(16.0),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.stretch,
              children: [
                Text('Total Prayers Missed'),
                SizedBox(height: 8),
                Text('$totalMissedPrayers'),
                SizedBox(height: 8),
                LinearProgressIndicator(
                  value: totalPrayersPossible > 0
                      ? totalMissedPrayers / totalPrayersPossible
                      : 0,
                  minHeight: 16,
                  backgroundColor: Colors.grey[300],
                  valueColor: AlwaysStoppedAnimation<Color>(Color(0xff34C759)),
                ),
                SizedBox(height: 16),
                Expanded(
                  child: GridView.count(
                    crossAxisCount: 2,
                    childAspectRatio: 1.5,
                    crossAxisSpacing: 12,
                    mainAxisSpacing: 12,
                    children: prayerProvider.prayers.map((prayer) {
                      return PrayerProgressCardMaterial(
                        prayer: prayer,
                        completedPrayers: prayerProvider
                            .getCompletedPrayersForMonth(2021, 2, prayer),
                        missedPrayers: prayerProvider.getMissedPrayersForMonth(
                            2021, 2, prayer),
                        completionPercentage: prayerProvider
                            .getPrayerCompletionPercentageForMonth(
                                2021, 2, prayer),
                        onPrayed: () {
                          // Implement the logic to mark the oldest missed prayer as done
                        },
                      );
                    }).toList(),
                  ),
                ),
              ],
            ),
          ),
        );
      },
    );
  }
}



