  set prayerData(Map<String, dynamic> value) {
    _prayerData = value;
    notifyListeners();
  }

  PrayerProvider(this._prayerService);

  Future<void> loadData() async {
    await _prayerService.loadData();
    _prayerData = _prayerService.prayerData;
    isDataLoaded = true;
    notifyListeners();
  }

  Future<void> initializeMonthlyPrayerStats(int year, int month) async {
    await _prayerService.initializeMonthlyPrayerStats(year, month);
    notifyListeners();
  }

  Future<void> updatePrayerStats(
      int year, int month, String prayer, bool done) async {
    await _prayerService.updatePrayerStats(year, month, prayer, done);
    notifyListeners();
  }

  int getCompletedPrayersForMonth(int year, int month, String prayer) {
    final key = '${year}_${month}_$prayer';
    return _prayerData.containsKey(key) ? _prayerData[key]['done'] : 0;
  }

  int getMissedPrayersForMonth(int year, int month, String prayer) {
    final key = '${year}_${month}_$prayer';
    return _prayerData.containsKey(key) ? _prayerData[key]['not_done'] : 0;
  }

  double getPrayerCompletionPercentageForMonth(
      int year, int month, String prayer) {
    final key = '${year}_${month}_$prayer';
    if (_prayerData.containsKey(key)) {
      final done = _prayerData[key]['done'];
      final total = done + _prayerData[key]['not_done'];
      return (done / total) * 100;
    }
    return 0.0;
  }

  Future<void> resetApplication() async {
    await _prayerService.clearData();
    _prayerData = {};
    notifyListeners();
  }

  Future<void> savePrayerData() async {
    await _prayerService.savePrayerData();
  }

  Future<void> saveData(int startYear, int startMonth, int endYear,
      int endMonth, Map<int, int> percentageMissedByYear) async {
    for (int year = startYear; year <= endYear; year++) {
      for (int month = 1; month <= 12; month++) {
        final keyPrefix = '${year}_${month}_';
        for (var prayer in prayers) {
          final key = keyPrefix + prayer;
          final totalDays = DateTime(year, month + 1, 0).day;
          final missedDays =
