class YearSelectionScreen extends StatefulWidget {
  const YearSelectionScreen({super.key});

  @override
  State<YearSelectionScreen> createState() => _YearSelectionScreenState();
}

class _YearSelectionScreenState extends State<YearSelectionScreen> {
  late TextEditingController _startYearController;
  late TextEditingController _startMonthController;
  late TextEditingController _endYearController;
  late TextEditingController _endMonthController;
  double _averageMissedPrayers = 25;

  @override
  void initState() {
    super.initState();
    final provider = Provider.of<PrayerProvider>(context, listen: false);
    _startYearController =
        TextEditingController(text: provider.startYear.toString());
    _startMonthController = TextEditingController(text: '1');
    _endYearController =
        TextEditingController(text: provider.endYear.toString());
    _endMonthController =
        TextEditingController(text: DateTime.now().month.toString());
  }

  @override
  void dispose() {
    _startYearController.dispose();
    _startMonthController.dispose();
    _endYearController.dispose();
    _endMonthController.dispose();
    super.dispose();
  }

  void _showHadithModal(BuildContext context) {
    Platform.isIOS
        ? showCupertinoDialog(
            context: context,
            builder: (BuildContext context) {
              return HadithModal();
            },
          )
        : showDialog(
            context: context,
            builder: (BuildContext context) {
              return HadithModal();
            },
          );
  }

  void _saveData() {
    int startYear =
        int.tryParse(_startYearController.text) ?? kDefaultStartYear;
    int startMonth = int.tryParse(_startMonthController.text) ?? 1;
    int endYear = int.tryParse(_endYearController.text) ?? kDefaultEndYear;
    int endMonth =
        int.tryParse(_endMonthController.text) ?? DateTime.now().month;

    Map<int, int> percentageMissedByYear = {};
    for (int year = startYear; year <= endYear; year++) {
      percentageMissedByYear[year] = _averageMissedPrayers.round();
    }

    var provider = Provider.of<PrayerProvider>(context, listen: false);
    provider.saveData(
        startYear, startMonth, endYear, endMonth, percentageMissedByYear);
    Navigator.pushAndRemoveUntil(
        context,
        MaterialPageRoute(builder: (context) => HomeScreen()),
        (route) => false);
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Amana Audit'),
      ),
      body: SingleChildScrollView(
        padding: REdgeInsets.only(top: 24, left: 18, right: 18),
        child: Column(
          crossAxisAlignment:
              kIsWeb ? CrossAxisAlignment.center : CrossAxisAlignment.stretch,
          children: [
            SizedBox(height: 24.h),
            Text(
              'ðŸ•Œ It\'s time to catch up on our prayers.',
              style: TextStyle(
                fontSize: 20,
                fontWeight: FontWeight.bold,
                color: Colors.black,
              ),
            ),
            Text(
              'ðŸ“… Select the range of years for which you want to track your missed prayers.',
              style: TextStyle(fontSize: 18, color: Colors.black),
            ),
            SizedBox(height: 24.h),
            Row(
              children: [
                Expanded(
                  child: AmanaInputField(
                    keyboardType: TextInputType.number,
                    controller: _startMonthController,
                    onChange: (val) {
                      setState(() {
                        _startMonthController.text = val;
                      });
                    },
                    sufixIcon: Icons.date_range_outlined,
                    label: "Start Month",
                  ),
                ),
                const SizedBox(width: 16),
                Expanded(
                  child: AmanaInputField(
                    keyboardType: TextInputType.number,
                    controller: _startYearController,
                    onChange: (val) {
                      setState(() {
                        _startYearController.text = val;
                      });
                    },
                    sufixIcon: Icons.date_range_outlined,
                    label: "Start Year",
                  ),
                ),
              ],
            ),
            const SizedBox(height: 16),
            Row(
              children: [
                Expanded(
                  child: AmanaInputField(
                    keyboardType: TextInputType.number,
                    controller: _endMonthController,
                    onChange: (val) {
                      setState(() {
                        _endMonthController.text = val;
                      });
                    },
                    sufixIcon: Icons.date_range_outlined,
                    label: "End Month",
                  ),
                ),
                const SizedBox(width: 16),
                Expanded(
                  child: AmanaInputField(
                    keyboardType: TextInputType.number,
                    controller: _endYearController,
                    onChange: (val) {
                      setState(() {
                        _endYearController.text = val;
                      });
                    },
                    sufixIcon: Icons.date_range_outlined,
                    label: "End Year",
                  ),
                ),
              ],
            ),
            SizedBox(height: 24.h),
            Text(
              'ðŸ•‹ Set the average percentage of prayers you estimate you missed for the selected years.',
              style: TextStyle(fontSize: 18, color: Colors.black),
            ),
            SizedBox(height: 16.h),
            Slider(
              value: _averageMissedPrayers,
              min: 0,
              max: 100,
              divisions: 20,
              label: '${_averageMissedPrayers.round()}%',
              onChanged: (double value) {
                setState(() {
                  _averageMissedPrayers = value;
                });
              },
            ),
            Text(
              '${_averageMissedPrayers.round()}% of prayers missed',
              style: TextStyle(fontSize: 16, color: Colors.black),
