class HomeScreen extends StatelessWidget {
  const HomeScreen({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Consumer<PrayerProvider>(
      builder: (context, prayerProvider, _) {
        if (!prayerProvider.isDataLoaded) {
          prayerProvider.loadData();
          return Scaffold(body: Center(child: CircularProgressIndicator()));
        }

        int totalPrayersPossible = prayerProvider.getTotalPrayersPossible();
        int totalMissedPrayers = prayerProvider.getTotalPrayersMissed();

        return Scaffold(
          appBar: AppBar(
            title: Text('Amana Audit'),
            actions: [
              IconButton(
                icon: Icon(Icons.edit),
                onPressed: () {
                  Navigator.push(
                    context,
                    MaterialPageRoute(
                        builder: (context) => EditDetailsScreen()),
                  );
                },
              ),
              IconButton(
                icon: Icon(Icons.refresh),
                onPressed: () async {
                  await prayerProvider.resetApplication();
                  prayerProvider.loadData();
                },
              ),
            ],
          ),
          body: Padding(
            padding: const EdgeInsets.all(16.0),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.stretch,
              children: [
                Text('Total Prayers Missed'),
                SizedBox(height: 8),
                Text('$totalMissedPrayers'),
                SizedBox(height: 8),
                LinearProgressIndicator(
                  value: totalPrayersPossible > 0
                      ? totalMissedPrayers / totalPrayersPossible
                      : 0,
                  minHeight: 16,
                  backgroundColor: Colors.grey[300],
                  valueColor: AlwaysStoppedAnimation<Color>(Color(0xff34C759)),
                ),
                SizedBox(height: 16),
                Expanded(
                  child: GridView.count(
                    crossAxisCount: 2,
                    childAspectRatio: 1.5,
                    crossAxisSpacing: 12,
                    mainAxisSpacing: 12,
                    children: prayerProvider.prayers.map((prayer) {
                      return PrayerProgressCardMaterial(prayer: prayer);
                    }).toList(),
                  ),
                ),
              ],
            ),
          ),
        );
      },
    );
  }
}

class PrayerProgressCardMaterial extends StatelessWidget {
  final String prayer;

  const PrayerProgressCardMaterial({required this.prayer});

  @override
  Widget build(BuildContext context) {
    final prayerProvider = Provider.of<PrayerProvider>(context);

    int totalPrayers = prayerProvider.getTotalPrayersForPrayerType(prayer);
    int completedPrayers =
        prayerProvider.getCompletedPrayersForPrayerType(prayer);
    int missedPrayers =
        prayerProvider.getTotalMissedPrayersForPrayerType(prayer);

    return Card(
      child: Padding(
        padding: const EdgeInsets.all(14.0),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          children: [
            Text(prayer,
                style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16)),
            ClipRRect(
              borderRadius: BorderRadius.circular(8),
              child: LinearProgressIndicator(
                value: totalPrayers > 0
                    ? (missedPrayers / totalPrayers).clamp(0, 1)
                    : 0,
                minHeight: 12,
                backgroundColor: Colors.grey[300],
                valueColor: AlwaysStoppedAnimation<Color>(Color(0xff34C759)),
              ),
            ),
            Text('${(missedPrayers / totalPrayers * 100).toStringAsFixed(1)}%',
                style: TextStyle(fontSize: 14)),
            Text('$missedPrayers / $totalPrayers',
                style: TextStyle(fontSize: 12)),
