```dart
import 'package:hive/hive.dart';

part 'prayer_data.g.dart';

@HiveType(typeId: 0)
class PrayerData {
  @HiveField(0)
  int year;

  @HiveField(1)
  int month;

  @HiveField(2)
  Map<String, int> prayersCompleted; // {'Fajr': 0, 'Dhuhr': 0, ...}

  @HiveField(3)
  Map<String, int> prayersMissed; // {'Fajr': 0, 'Dhuhr': 0, ...}

  @HiveField(4)
  int totalPrayed;

  @HiveField(5)
  int totalMissed;

  @HiveField(6)
  double percentagePrayed;

  @HiveField(7)
  double percentageMissed;

  PrayerData({
    required this.year,
    required this.month,
    required this.prayersCompleted,
    required this.prayersMissed,
    this.totalPrayed = 0,
    this.totalMissed = 0,
    this.percentagePrayed = 0.0,
    this.percentageMissed = 0.0,
  }) {
    _calculateTotals();
  }

  void _calculateTotals() {
    totalPrayed = prayersCompleted.values.reduce((a, b) => a + b);
    totalMissed = prayersMissed.values.reduce((a, b) => a + b);
    int totalPrayers = totalPrayed + totalMissed;
    percentagePrayed = (totalPrayed / totalPrayers) * 100;
    percentageMissed = (totalMissed / totalPrayers) * 100;
  }

  void updatePrayer(String prayer, int completed, int missed) {
    prayersCompleted[prayer] = completed;
    prayersMissed[prayer] = missed;
    _calculateTotals();
  }
}
