import 'hive_service.dart';
import 'package:share_plus/share_plus.dart';

class PrayerService {
  final HiveService _hiveService;
  bool isDataLoaded = false;
  final List<String> prayers = ['Fajr', 'Dhuhr', 'Asr', 'Maghrib', 'Isha'];
  Map<String, dynamic> prayerData = {};

  PrayerService(this._hiveService);

  Future<void> loadData() async {
    var loadedData = await _hiveService.loadData('prayerData');

    if (loadedData != null && loadedData is Map) {
      prayerData = Map<String, dynamic>.from(loadedData.cast<String, dynamic>());
    } else {
      _setDefaultPrayerData();
      savePrayerData();
    }

    isDataLoaded = true;
  }

  Future<void> updatePrayerData(String prayer, int year, int month, int completed, int missed) async {
    if (prayerData['$year-$month'] == null) {
      prayerData['$year-$month'] = PrayerData(
        year: year,
        month: month,
        prayersCompleted: {for (var p in prayers) p: 0},
        prayersMissed: {for (var p in prayers) p: 0},
      );
    }
    prayerData['$year-$month'].updatePrayer(prayer, completed, missed);
    await savePrayerData();
  }

  Future<void> incrementPrayer(String prayer) async {
    for (int year = prayerData['startYear']; year <= prayerData['endYear']; year++) {
      for (int month = 1; month <= 12; month++) {
        var prayerDataForMonth = prayerData['$year-$month'];
        if (prayerDataForMonth == null) {
          prayerDataForMonth = PrayerData(
            year: year,
            month: month,
            prayersCompleted: {for (var p in prayers) p: 0},
            prayersMissed: {for (var p in prayers) p: getTotalPrayersForMonth(year, month)},
          );
          prayerData['$year-$month'] = prayerDataForMonth;
        }
        if (prayerDataForMonth.prayersCompleted[prayer]! < getTotalPrayersForMonth(year, month)) {
          prayerDataForMonth.prayersCompleted[prayer] = prayerDataForMonth.prayersCompleted[prayer]! + 1;
          prayerDataForMonth.prayersMissed[prayer] = getTotalPrayersForMonth(year, month) - prayerDataForMonth.prayersCompleted[prayer]!;
          prayerDataForMonth._calculateTotals();
          await savePrayerData();
          return;
        }
      }
    }
  }

  int getTotalPrayersForMonth(int year, int month) {
    int daysInMonth = DateTime(year, month + 1, 0).day;
    return daysInMonth;
  }

  Future<void> savePrayerData() async {
    await _hiveService.saveData('prayerData', prayerData);
  }

  void _setDefaultPrayerData() {
    final currentYear = DateTime.now().year;
    prayerData = {
      'metadata': {
        'createdAt': DateTime.now().toIso8601String(),
        'updatedAt': DateTime.now().toIso8601String(),
