import '../platform_specific_widget/amana_input_field.dart';
import '../providers/prayer_provider.dart';
import 'home_screen.dart';

class EditDetailsScreen extends StatefulWidget {
  const EditDetailsScreen({super.key});

  @override
  _EditDetailsScreenState createState() => _EditDetailsScreenState();
}

class _EditDetailsScreenState extends State<EditDetailsScreen> {
  int? _selectedYear;

  String? _selectedMonth;
  final Map<String, TextEditingController> _controllers = {};

  @override
  void initState() {
    super.initState();
    final prayerProvider = Provider.of<PrayerProvider>(context, listen: false);
    for (var prayer in prayerProvider.prayers) {
      _controllers[prayer] = TextEditingController();
    }
  }

  @override
  void dispose() {
    _controllers.forEach((_, controller) => controller.dispose());
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    final prayerProvider = Provider.of<PrayerProvider>(context);

    return AmanaScaffold(
      title: "Edit Details",
      body: SingleChildScrollView(
        child: Padding(
          padding: REdgeInsets.all(16.0),
          child: Column(
            crossAxisAlignment:
                kIsWeb ? CrossAxisAlignment.center : CrossAxisAlignment.stretch,
            children: [
              Text(
                'Pick each year and reflect, add what percentage you estimate you missed that year.',
                style: TextStyle(fontSize: 18, color: Colors.black),
              ),
              SizedBox(height: 20.h),
              _buildYearDropdown(prayerProvider),
              SizedBox(height: 20.h),
              _selectedYear != null && _selectedYear != 0
                  ? _buildMonthDropDown(prayerProvider)
                  : Text("Selecting Month"),
              SizedBox(
                height: 20.h,
              ),
              if (_selectedYear != null &&
                  _selectedYear != 0 &&
                  _selectedMonth != null &&
                  _selectedMonth != "")
                ...prayerProvider.prayers
                    .map((prayer) => _buildPrayerRow(prayerProvider, prayer)),
              SizedBox(height: 16.h),
              kIsWeb
                  ? AmanaButton(
                      onPress: () {
                        for (var prayer in prayerProvider.prayers) {
                          final percentage =
                              int.tryParse(_controllers[prayer]!.text) ?? 0;
                          prayerProvider.updatePrayerData(
                            prayer,
                            _selectedYear!,
                            (365 * percentage / 100).round(),
                            (365 - (365 * percentage / 100)).round(),
                            percentage,
                          );
                        }
                        prayerProvider.savePrayerData();
                        Navigator.pushAndRemoveUntil(
                            context,
                            MaterialPageRoute(
                                builder: (context) => HomeScreen()),
                            (route) => false);
                      },
                      label: "Save",
                      isAppLevel: true)
                  : AmanaButton(
                      onPress: () {
                        for (var prayer in prayerProvider.prayers) {
                          final percentage =
                              int.tryParse(_controllers[prayer]!.text) ?? 0;
                          prayerProvider.updatePrayerData(
                            prayer,
                            _selectedYear!,
                            (365 * percentage / 100).round(),
                            (365 - (365 * percentage / 100)).round(),
                            percentage,
                          );
                        }
                        prayerProvider.savePrayerData();
                        Navigator.pushAndRemoveUntil(
                            context,
                            MaterialPageRoute(
                                builder: (context) => HomeScreen()),
                            (route) => false);
                      },
                      label: "Save",
                      isAppLevel: true),
              SizedBox(height: 16.h),
            ],
          ),
        ),
      ),
    );
  }

  Widget _buildYearDropdown(PrayerProvider prayerProvider) {
    final startYear = prayerProvider.startYear;
    final endYear = prayerProvider.endYear;

    return AmanaDropDown(
      value: _selectedYear,
      items: List.generate(endYear - startYear + 2, (index) {
        final year = (index == 0) ? 0 : startYear + (index - 1);
        return {
          "value": year,
          "child": year == 0 ? Text("Select Year") : Text('$year'),
        };
      }),
      onChanged: (newValue) {
        setState(() {
          _selectedYear = newValue;
        });
      },
      labelText: 'Select Year',
    );
  }

  Widget _buildMonthDropDown(PrayerProvider provider) {
    return AmanaMonthDropDown(
        items: List.generate(13, (index) {
          final month = (index == 0) ? "" : provider.monthsList[index - 1];
          return {
            "value": month,
            "child": month == "" ? Text("Select Month") : Text('$month'),
          };
        }),
        onChanged: (val) {
          setState(() {
            _selectedMonth = val.toString();
          });
        },
        value: _selectedMonth ?? "",
        labelText: "Select Month");
  }

  Widget _buildPresetOptions(PrayerProvider provider) {
    return Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: [
        ListTile(
          leading: Icon(Icons.check_circle_outline),
          title: Text('Set all years to a specific percentage'),
          onTap: () {
            showDialog(
              context: context,
              builder: (context) {
                int percentage = 50;
                return AlertDialog(
                  title: Text('Set Percentage for All Years'),
                  content: StatefulBuilder(
                    builder: (context, setState) {
                      return Slider(
                        value: percentage.toDouble(),
                        min: 0,
                        max: 100,
                        divisions: 20,
                        label: '$percentage%',
                        onChanged: (value) {
                          setState(() {
                            percentage = value.round();
                          });
                        },
                      );
                    },
                  ),
                  actions: [
                    TextButton(
                      onPressed: () {
                        Navigator.pop(context);
                      },
                      child: Text('Cancel'),
                    ),
                    TextButton(
                      onPressed: () {
                        Map<int, int> percentageMissedByYear = {};
                        for (int year = provider.startYear;
                            year <= provider.endYear;
                            year++) {
                          percentageMissedByYear[year] = percentage;
                        }
                        provider.applyPresetOption(percentageMissedByYear);
                        Navigator.pop(context);
                      },
                      child: Text('Apply'),
                    ),
                  ],
                );
              },
            );
          },
        ),
      ],
    );
  }

  Widget _buildPrayerRow(PrayerProvider prayerProvider, String prayer) {
    final prayerData =
        prayerProvider.getPrayerData()['yearlyData'][_selectedYear.toString()];

    // Add null check here
    if (prayerData == null || prayerData[prayer] == null) {
      return Container();
    }

    final prayerPercentage = prayerData[prayer]['percentMissed'];

    return Padding(
      padding: REdgeInsets.symmetric(vertical: 4.0),
      child: Row(
        mainAxisAlignment: MainAxisAlignment.spaceBetween,
        children: [
          Expanded(
              flex: 2,
              child: Text(
                prayer,
                style: TextStyle(color: Colors.black),
              )),
          Expanded(
            child: AmanaInputField(
              controller: _controllers[prayer]!
                ..text = prayerPercentage.toString(),
              sufixIcon: Icons.percent_outlined,
              onChange: (val) {},
              label: "Missed",
              keyboardType: TextInputType.number,
            ),
          ),
        ],
      ),
    );
  }
}



