class PrayerProgressCardCupertino extends StatelessWidget {
  final String prayer;

  const PrayerProgressCardCupertino({required this.prayer});

  @override
  Widget build(BuildContext context) {
    final prayerProvider = Provider.of<PrayerProvider>(context);

    int year = DateTime.now().year;
    int month = DateTime.now().month;
    int completedPrayers =
        prayerProvider.getCompletedPrayersForMonth(year, month, prayer);
    int missedPrayers =
        prayerProvider.getMissedPrayersForMonth(year, month, prayer);
    double completionPercentage = prayerProvider
        .getPrayerCompletionPercentageForMonth(year, month, prayer);

    return CupertinoCard(
      child: Padding(
        padding: const EdgeInsets.all(14.0),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          children: [
            Text(prayer,
                style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16)),
            ClipRRect(
              borderRadius: BorderRadius.circular(8),
              child: LinearProgressIndicator(
                value: completionPercentage / 100,
                minHeight: 12,
                backgroundColor: Colors.grey[300],
                valueColor: AlwaysStoppedAnimation<Color>(Color(0xff34C759)),
              ),
            ),
            Text('${completionPercentage.toStringAsFixed(1)}%',
                style: TextStyle(fontSize: 14)),
            Text('$completedPrayers / ${completedPrayers + missedPrayers}',
                style: TextStyle(fontSize: 12)),
            CupertinoButton(
              onPressed: () =>
                  prayerProvider.updatePrayerStats(year, month, prayer, true),
              child: Text('Pray'),
            ),
          ],
        ),
      ),
    );
  }
}

