class HomeScreen extends StatelessWidget {
  final HiveService _hiveService = HiveService();

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Prayer Tracker'),
      ),
      body: ValueListenableBuilder(
        valueListenable: _hiveService.prayerBox.listenable(),
        builder: (context, Box<PrayerRecord> box, _) {
          final records = _hiveService.getPrayerRecords();
          final totalPrayed =
              records.fold(0, (sum, record) => sum + record.totalPrayed);
          final totalPossible = records.length * 30 * 5;

          return Column(
            children: [
              LinearProgressIndicator(value: totalPrayed / totalPossible),
              Text('$totalPrayed / $totalPossible'),
              ...['Fajr', 'Dhuhr', 'Asr', 'Maghrib', 'Isha'].map((prayer) {
                return PrayerProgressButton(prayer: prayer);
              }).toList(),
            ],
          );
        },
      ),
    );
  }
}

