```dart
// File: providers/prayer_provider.dart

import 'package:flutter/foundation.dart';
import '../services/prayer_service.dart';
import '../models/prayer_data.dart';

class PrayerProvider with ChangeNotifier {
  final PrayerService _prayerService;
  Map<String, PrayerData> _prayerData = {};

  PrayerProvider(this._prayerService);

  Map<String, PrayerData> get prayerData => _prayerData;

  Future<void> loadData() async {
    await _prayerService.loadData();
    _prayerData = _prayerService.prayerData;
    notifyListeners();
  }

  Future<void> saveData(PrayerData data) async {
    await _prayerService.savePrayerData();
    _prayerData['${data.year}-${data.month}'] = data;
    notifyListeners();
  }

  void incrementPrayer(String prayer) {
    _prayerService.incrementPrayer(prayer);
    notifyListeners();
  }

  int getCompletedPrayersForMonth(int year, int month, String prayer) {
    final key = '$year-$month';
    if (!_prayerData.containsKey(key)) return 0;
    return _prayerData[key]!.prayersCompleted[prayer] ?? 0;
  }

  int getMissedPrayersForMonth(int year, int month, String prayer) {
    final key = '$year-$month';
    if (!_prayerData.containsKey(key)) return 0;
    return _prayerData[key]!.prayersMissed[prayer] ?? 0;
  }

  double getPrayerCompletionPercentageForMonth(
      int year, int month, String prayer) {
    final key = '$year-$month';
    if (!_prayerData.containsKey(key)) return 0.0;
    final data = _prayerData[key]!;
    final total = data.prayersCompleted[prayer]! + data.prayersMissed[prayer]!;
    return total == 0 ? 0.0 : (data.prayersCompleted[prayer]! / total) * 100;
  }

  void updatePrayerStats(
      int year, int month, String prayer, bool increaseCompletedCount) {
    final key = '$year-$month';
    if (_prayerData.containsKey(key)) {
      final data = _prayerData[key]!;
      if (increaseCompletedCount) {
        data.prayersCompleted[prayer] = data.prayersCompleted[prayer]! + 1;
        data.prayersMissed[prayer] = data.prayersMissed[prayer]! - 1;
      } else {
        data.prayersCompleted[prayer] = data.prayersCompleted[prayer]! - 1;
        data.prayersMissed[prayer] = data.prayersMissed[prayer]! + 1;
      }
      _updateTotals(data);
      saveData(data);
    }
  }

  void _updateTotals(PrayerData data) {
    data.totalPrayed = data.prayersCompleted.values.reduce((a, b) => a + b);
    data.totalMissed = data.prayersMissed.values.reduce((a, b) => a + b);
    data.percentagePrayed =
        data.totalPrayed / (data.totalPrayed + data.totalMissed) * 100;
    data.percentageMissed = 100 - data.percentagePrayed;
  }
}
