    int endMonth =
        int.tryParse(_endMonthController.text) ?? DateTime.now().month;

    Map<int, int> percentageMissedByYear = {};
    for (int year = startYear; year <= endYear; year++) {
      percentageMissedByYear[year] = _averageMissedPrayers.round();
