// File: lib/screens/home_screen.dart

import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import '../providers/prayer_provider.dart';
import 'prayer_progress_card_material.dart';

class HomeScreen extends StatelessWidget {
  const HomeScreen({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Consumer<PrayerProvider>(
      builder: (context, prayerProvider, _) {
        if (!prayerProvider.isDataLoaded) {
          prayerProvider.loadData();
          return Scaffold(body: Center(child: CircularProgressIndicator()));
        }

        final currentYear = DateTime.now().year;
        final currentMonth = DateTime.now().month;
        final prayers = prayerProvider.prayers;

        int totalPrayed = 0;
        int totalMissed = 0;
        prayers.forEach((prayer) {
          totalPrayed += prayerProvider.getCompletedPrayersForMonth(
              currentYear, currentMonth, prayer);
          totalMissed += prayerProvider.getMissedPrayersForMonth(
              currentYear, currentMonth, prayer);
        });
        final totalPrayersPossible = totalPrayed + totalMissed;

        return Scaffold(
          appBar: AppBar(
            title: Text('Prayer Tracker'),
            actions: [
              IconButton(
                icon: Icon(Icons.edit),
                onPressed: () {
                  Navigator.pushNamed(context, '/edit-details');
                },
              ),
            ],
          ),
          body: Padding(
            padding: const EdgeInsets.all(16.0),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.stretch,
              children: [
                LinearProgressIndicator(
                  value: totalPrayersPossible > 0
                      ? totalPrayed / totalPrayersPossible
                      : 0,
                  minHeight: 16,
                  backgroundColor: Colors.grey[300],
                  valueColor: AlwaysStoppedAnimation<Color>(Color(0xff34C759)),
                ),
                SizedBox(height: 8),
                Text('$totalPrayed / $totalPrayersPossible'),
                SizedBox(height: 16),
                Expanded(
                  child: GridView.count(
                    crossAxisCount: 2,
                    childAspectRatio: 1.5,
                    crossAxisSpacing: 12,
                    mainAxisSpacing: 12,
                    children: prayers.map((prayer) {
                      return PrayerProgressCardMaterial(
                        prayer: prayer,
                        year: currentYear,
                        month: currentMonth,
                      );
                    }).toList(),
                  ),
                ),
              ],
            ),
          ),
        );
      },
    );
  }
}



