import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import '../providers/prayer_provider.dart';
import '../models/prayer_data.dart';

class EditDetailsScreen extends StatefulWidget {
  const EditDetailsScreen({Key? key}) : super(key: key);

  @override
  _EditDetailsScreenState createState() => _EditDetailsScreenState();
}

class _EditDetailsScreenState extends State<EditDetailsScreen> {
  int _selectedYear = DateTime.now().year;
  int _selectedMonth = DateTime.now().month;

  @override
  Widget build(BuildContext context) {
    final prayerProvider = Provider.of<PrayerProvider>(context);

    return Scaffold(
      appBar: AppBar(
        title: Text('Edit Details'),
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.stretch,
          children: [
            DropdownButtonFormField<int>(
              value: _selectedYear,
              items: List.generate(10, (index) => DateTime.now().year - index)
                  .map((year) => DropdownMenuItem(
                        value: year,
                        child: Text(year.toString()),
                      ))
                  .toList(),
              onChanged: (year) {
                setState(() {
                  _selectedYear = year!;
                });
              },
              decoration: InputDecoration(
                labelText: 'Select Year',
              ),
            ),
            SizedBox(height: 16),
            DropdownButtonFormField<int>(
              value: _selectedMonth,
              items: List.generate(12, (index) => index + 1)
                  .map((month) => DropdownMenuItem(
                        value: month,
                        child: Text(month.toString()),
                      ))
                  .toList(),
              onChanged: (month) {
                setState(() {
                  _selectedMonth = month!;
                });
              },
              decoration: InputDecoration(
                labelText: 'Select Month',
              ),
            ),
            SizedBox(height: 16),
            Expanded(
              child: FutureBuilder<PrayerData>(
                future: prayerProvider.getPrayerDataForMonth(
                    _selectedYear, _selectedMonth),
                builder: (context, snapshot) {
                  if (snapshot.connectionState == ConnectionState.waiting) {
                    return Center(child: CircularProgressIndicator());
                  }
                  if (!snapshot.hasData) {
                    return Center(child: Text('No data available'));
                  }
                  final prayerData = snapshot.data!;
                  return ListView(
                    children: prayerProvider.prayers.map((prayer) {
                      final completed =
                          prayerData.prayersCompleted[prayer] ?? 0;
                      final missed = prayerData.prayersMissed[prayer] ?? 0;
                      return ListTile(
                        title: Text(prayer),
                        subtitle:
                            Text('Completed: $completed, Missed: $missed'),
                        trailing: Row(
                          mainAxisSize: MainAxisSize.min,
                          children: [
                            IconButton(
                              icon: Icon(Icons.remove),
                              onPressed: () {
                                prayerProvider.updatePrayerStats(
                                  _selectedYear,
                                  _selectedMonth,
                                  prayer,
                                  false,
                                );
                              },
                            ),
                            IconButton(
                              icon: Icon(Icons.add),
                              onPressed: () {
                                prayerProvider.updatePrayerStats(
                                  _selectedYear,
                                  _selectedMonth,
                                  prayer,
                                  true,
                                );
                              },
                            ),
                          ],
                        ),
                      );
                    }).toList(),
                  );
                },
              ),
            ),
          ],
        ),
      ),
    );
  }
}



