      prayersMissed: (fields[3] as Map).cast<String, int>(),
      totalPrayed: fields[4] as int,
      totalMissed: fields[5] as int,
      percentagePrayed: fields[6] as double,
      percentageMissed: fields[7] as double,
    );
  }

  @override
  void write(BinaryWriter writer, PrayerData obj) {
    writer
      ..writeByte(8)
      ..writeByte(0)
      ..write(obj.year)
      ..writeByte(1)
      ..write(obj.month)
      ..writeByte(2)
      ..write(obj.prayersCompleted)
      ..writeByte(3)
      ..write(obj.prayersMissed)
      ..writeByte(4)
      ..write(obj.totalPrayed)
      ..writeByte(5)
      ..write(obj.totalMissed)
      ..writeByte(6)
      ..write(obj.percentagePrayed)
      ..writeByte(7)
      ..write(obj.percentageMissed);
  }

  @override
  int get hashCode => typeId.hashCode;

  @override
  bool operator ==(Object other) =>
      identical(this, other) ||
      other is PrayerDataAdapter &&
          runtimeType == other.runtimeType &&
          typeId == other.typeId;
}



