@HiveType(typeId: 0)
class PrayerData {
  @HiveField(0)
  int year;

  @HiveField(1)
  int month;

  @HiveField(2)
  Map<String, int> prayersCompleted; // {'Fajr': 0, 'Dhuhr': 0, ...}

  @HiveField(3)
  Map<String, int> prayersMissed; // {'Fajr': 0, 'Dhuhr': 0, ...}

  @HiveField(4)
  int totalPrayed;

  @HiveField(5)
  int totalMissed;

  @HiveField(6)
  double percentagePrayed;

  @HiveField(7)
  double percentageMissed;

  PrayerData({
    required this.year,
    required this.month,
    required this.prayersCompleted,
    required this.prayersMissed,
    required this.totalPrayed,
    required this.totalMissed,
    required this.percentagePrayed,
    required this.percentageMissed,
  });

  factory PrayerData.fromMap(Map<String, dynamic> map) {
    return PrayerData(
      year: map['year'],
      month: map['month'],
      prayersCompleted: Map<String, int>.from(map['prayersCompleted']),
      prayersMissed: Map<String, int>.from(map['prayersMissed']),
      totalPrayed: map['totalPrayed'],
      totalMissed: map['totalMissed'],
      percentagePrayed: map['percentagePrayed'],
      percentageMissed: map['percentageMissed'],
    );
  }

  Map<String, dynamic> toMap() {
    return {
      'year': year,
      'month': month,
      'prayersCompleted': prayersCompleted,
      'prayersMissed': prayersMissed,
      'totalPrayed': totalPrayed,
      'totalMissed': totalMissed,
      'percentagePrayed': percentagePrayed,
      'percentageMissed': percentageMissed,
    };
  }
}



