class PrayerProgressCardMaterial extends StatelessWidget {
  final String prayer;
  final int year;
  final int month;

  const PrayerProgressCardMaterial({
    required this.prayer,
    required this.year,
    required this.month,
  });

  @override
  Widget build(BuildContext context) {
    final prayerProvider = Provider.of<PrayerProvider>(context);

    return FutureBuilder<int>(
      future: Future.value(prayerProvider.getCompletedPrayersForMonth(
          year, month, prayer)), // Fix: Wrap with Future.value()
      builder: (context, completedSnapshot) {
        return FutureBuilder<int>(
          future: Future.value(prayerProvider.getMissedPrayersForMonth(
              year, month, prayer)), // Fix: Wrap with Future.value()
          builder: (context, missedSnapshot) {
            if (completedSnapshot.connectionState == ConnectionState.waiting ||
                missedSnapshot.connectionState == ConnectionState.waiting) {
              return CircularProgressIndicator();
            }
            final completedPrayers = completedSnapshot.data ?? 0;
            final missedPrayers = missedSnapshot.data ?? 0;
            final totalPrayers = completedPrayers + missedPrayers;

            return Card(
              child: Padding(
                padding: const EdgeInsets.all(14.0),
                child: Column(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text(prayer,
