  @HiveField(0)
  int year;

  @HiveField(1)
  int month;

  @HiveField(2)
  int fajrMissed;

  @HiveField(3)
  int dhuhrMissed;

  @HiveField(4)
  int asrMissed;

  @HiveField(5)
  int maghribMissed;

  @HiveField(6)
  int ishaMissed;

  // Derived fields
  int get totalMissed =>
      fajrMissed + dhuhrMissed + asrMissed + maghribMissed + ishaMissed;
  int get totalPrayed => (30 * 5) - totalMissed; // Assuming 30 days in a month

  double get fajrPercentage => fajrMissed / 30;
  double get dhuhrPercentage => dhuhrMissed / 30;
  double get asrPercentage => asrMissed / 30;
  double get maghribPercentage => maghribMissed / 30;
  double get ishaPercentage => ishaMissed / 30;
}



