  double _percentageCompleted = 0;

  @override
  void didChangeDependencies() {
    super.didChangeDependencies();
    final prayerProvider = Provider.of<PrayerProvider>(context, listen: false);
    if (_selectedYear != null && _selectedMonth != null) {
      _percentageCompleted = prayerProvider.getPrayerCompletionPercentageForMonth(
        _selectedYear!,
        _selectedMonth!,
      );
    } else if (_selectedYear != null) {
      _percentageCompleted = prayerProvider.getPrayerCompletionPercentageForYear(
        _selectedYear!,
      );
    }
  }

  @override
  Widget build(BuildContext context) {
    final prayerProvider = Provider.of<PrayerProvider>(context);

    return Scaffold(
      appBar: AppBar(
        title: Text('Edit Details'),
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.stretch,
          children: [
            DropdownButtonFormField<int>(
              value: _selectedYear,
              items: [
                DropdownMenuItem(
                  child: Text('Select Year'),
                  value: null,
                ),
                ...List.generate(10, (index) => DateTime.now().year - index)
                    .map((year) => DropdownMenuItem(
                          value: year,
                          child: Text(year.toString()),
                        ))
                    .toList(),
              ],
              onChanged: (year) {
                setState(() {
                  _selectedYear = year;
                  _selectedMonth = null;
                  if (year != null) {
                    _percentageCompleted = prayerProvider.getPrayerCompletionPercentageForYear(year);
                  }
                });
              },
              decoration: InputDecoration(
                labelText: 'Select Year',
              ),
            ),
            SizedBox(height: 16),
            if (_selectedYear != null)
              DropdownButtonFormField<int>(
                value: _selectedMonth,
                items: [
                  DropdownMenuItem(
                    child: Text('Average for total year, or select month'),
                    value: null,
                  ),
                  ...List.generate(
                      _selectedYear == DateTime.now().year
                          ? DateTime.now().month - 1
                          : 12, (index) => index + 1)
                      .map((month) => DropdownMenuItem(
                            value: month,
                            child: Text('Month $month'),
                          ))
                      .toList(),
                ],
                onChanged: (month) {
                  setState(() {
                    _selectedMonth = month;
                    if (month != null) {
                      _percentageCompleted = prayerProvider.getPrayerCompletionPercentageForMonth(
                        _selectedYear!,
                        month,
                      );
                    } else {
                      _percentageCompleted = prayerProvider.getPrayerCompletionPercentageForYear(
                        _selectedYear!,
                      );
                    }
                  });
                },
                decoration: InputDecoration(
                  labelText: 'Select Month',
                ),
              ),
            SizedBox(height: 16),
            Slider(
              value: _percentageCompleted,
              min: 0,
              max: 100,
              divisions: 20,
              label: '${_percentageCompleted.round()}%',
              onChanged: (value) {
                setState(() {
                  _percentageCompleted = value;
                });
              },
              onChangeEnd: (value) {
                if (_selectedYear != null) {
                  if (_selectedMonth != null) {
                    prayerProvider.updatePrayerStatsForMonth(
                      _selectedYear!,
                      _selectedMonth!,
                      _percentageCompleted,
                    );
                  } else {
                    prayerProvider.updatePrayerStatsForYear(
                      _selectedYear!,
                      _percentageCompleted,
                    );
                  }
                }
              },
            ),
          ],
        ),
      ),
    );
  }
}
