  Widget build(BuildContext context) {
    return Theme.of(context).platform == TargetPlatform.iOS
        ? CupertinoButton(
            color: CupertinoColors.systemGreen,
            child: Text(
                (value == null || value == 0 ? labelText : value.toString())),
