import '../services/prayer_service.dart';
import '../models/prayer_data.dart';
import '../constants.dart';

class PrayerProvider with ChangeNotifier {
  final PrayerService _prayerService;

  PrayerProvider(this._prayerService) {
    _prayerService.onPrayerDataUpdated = _onPrayerDataUpdated;
  }

  void _onPrayerDataUpdated() {
    notifyListeners();
  }

  Map<String, PrayerData> _prayerData = {};
  Map<String, PrayerData> get prayerData => _prayerData;

  int get startYear => kDefaultStartYear;
  int get endYear => kDefaultEndYear;

  bool get isDataLoaded => _prayerService.isDataLoaded;
  List<String> get prayers => _prayerService.prayers;

  Future<void> loadData() async {
    await _prayerService.loadData();
    _prayerData = _prayerService.prayerData;
    notifyListeners();
  }

  Future<void> saveData(PrayerData data) async {
    await _prayerService.savePrayerData(data);
    _prayerData['${data.year}-${data.month}'] = data;
    notifyListeners();
  }

  void incrementPrayer(String prayer) {
    _prayerService.incrementPrayer(prayer);
    notifyListeners();
  }

  int getCompletedPrayersForMonth(int year, int month, String prayer) {
    return _prayerService.getCompletedPrayersForMonth(year, month, prayer);
  }

  int getMissedPrayersForMonth(int year, int month, String prayer) {
    return _prayerService.getMissedPrayersForMonth(year, month, prayer);
  }

  Future<PrayerData> getPrayerDataForMonth(int year, int month) async {
    return await _prayerService.getPrayerDataForMonth(year, month);
  }

  void updatePrayerStats(
      int year, int month, String prayer, bool increaseCompletedCount) {
    _prayerService.updatePrayerStats(
        year, month, prayer, increaseCompletedCount);
    notifyListeners();
  }

  void updatePrayerStatsForYear(int year, double percentageCompleted) {
    _prayerService.updatePrayerStatsForYear(year, percentageCompleted);
    notifyListeners();
  }

  void updatePrayerStatsForMonth(
      int year, int month, double percentageCompleted) {
    _prayerService.updatePrayerStatsForMonth(year, month, percentageCompleted);
    notifyListeners();
  }

  double getPrayerCompletionPercentageForMonth(int year, int month) {
    return _prayerService.getPrayerCompletionPercentageForMonth(year, month);
  }

  double getPrayerCompletionPercentageForYear(int year) {
    return _prayerService.getPrayerCompletionPercentageForYear(year);
  }

  Future<Map<String, dynamic>> getAggregatedPrayerData() async {
    return await _prayerService.getAggregatedPrayerData();
  }

  void updateStartAndEndYear(int startYear, int endYear) {
    _prayerService.updateStartAndEndYear(startYear, endYear);
    notifyListeners();
  }
}



