      builder: (context, completedSnapshot) {
        return FutureBuilder<int>(
          future: Future.value(
              prayerProvider.getMissedPrayersForMonth(year, month, prayer)),
          builder: (context, missedSnapshot) {
            if (completedSnapshot.connectionState == ConnectionState.waiting ||
                missedSnapshot.connectionState == ConnectionState.waiting) {
              return CupertinoActivityIndicator();
            }
            final completedPrayers = completedSnapshot.data ?? 0;
            final missedPrayers = missedSnapshot.data ?? 0;
            final totalPrayers = completedPrayers + missedPrayers;

            return Container(
              margin: EdgeInsets.all(8.0),
              padding: EdgeInsets.all(14.0),
              decoration: BoxDecoration(
                color: CupertinoColors.white,
                borderRadius: BorderRadius.circular(10),
                boxShadow: [
                  BoxShadow(
                    color: CupertinoColors.systemGrey.withOpacity(0.4),
                    spreadRadius: 1,
                    blurRadius: 3,
                    offset: Offset(0, 2),
                  ),
                ],
              ),
              child: Column(
                mainAxisAlignment: MainAxisAlignment.spaceBetween,
                children: [
                  Text(prayer,
                      style:
                          TextStyle(fontWeight: FontWeight.bold, fontSize: 16)),
                  SizedBox(height: 8),
                  ClipRRect(
                    borderRadius: BorderRadius.circular(8),
                    child: LinearProgressIndicator(
                      value: totalPrayers > 0
                          ? completedPrayers / totalPrayers
                          : 0,
                      minHeight: 12,
                      backgroundColor: CupertinoColors.systemGrey4,
                      valueColor: AlwaysStoppedAnimation<Color>(
                          CupertinoColors.activeGreen),
                    ),
                  ),
                  SizedBox(height: 8),
                  Text(
                      '${(completedPrayers / totalPrayers * 100).toStringAsFixed(1)}%',
                      style: TextStyle(fontSize: 14)),
                  Text('$completedPrayers / $totalPrayers',
                      style: TextStyle(fontSize: 12)),
                  SizedBox(height: 8),
                  CupertinoButton.filled(
                    onPressed: () => prayerProvider.incrementPrayer(prayer),
                    child: Text('Pray'),
                  ),
                ],
