import 'hive_service.dart';
import 'package:share_plus/share_plus.dart';
import '../models/prayer_data.dart';
import '../constants.dart';

typedef PrayerDataUpdateCallback = void Function();

class PrayerService {
  final HiveService _hiveService;
  bool isDataLoaded = false;
  final List<String> prayers = ['Fajr', 'Dhuhr', 'Asr', 'Maghrib', 'Isha'];
  Map<String, PrayerData> prayerData = {};
  PrayerDataUpdateCallback? onPrayerDataUpdated;

  PrayerService(this._hiveService);

  Future<void> loadData() async {
    var loadedData = await _hiveService.loadData('prayerData');
    ;

    if (loadedData != null && loadedData is Map<String, Map<String, dynamic>>) {
      prayerData = Map<String, PrayerData>.from(
        loadedData
            .map((key, value) => MapEntry(key, PrayerData.fromMap(value))),
      );
    }

    if (prayerData.isEmpty) {
      for (int year = kDefaultStartYear; year <= kDefaultEndYear; year++) {
        for (int month = 1; month <= 12; month++) {
          prayerData['$year-$month'] = _calculatePrayerData(
              year, month, 100 - kDefaultPercentageMissed.toDouble());
        }
      }
      await savePrayerData(prayerData.values.first);
    }

    isDataLoaded = true;
    print('Loaded data from Hive: $prayerData');
  }

  Future<void> savePrayerData(PrayerData data) async {
    prayerData['${data.year}-${data.month}'] = data;
    await _hiveService.saveData('prayerData', prayerData);

    Map<String, Map<String, dynamic>> unpacked = {};
    prayerData.forEach((key, value) {
      unpacked[key] = {
        'year': value.year,
        'month': value.month,
        'prayersCompleted': value.prayersCompleted,
        'prayersMissed': value.prayersMissed,
        'totalPrayed': value.totalPrayed,
        'totalMissed': value.totalMissed,
        'percentagePrayed': value.percentagePrayed,
        'percentageMissed': value.percentageMissed,
      };
    });
    print('Updated data: $unpacked');
    print('Saved data to Hive: $prayerData');

    onPrayerDataUpdated?.call();
  }

  void incrementPrayer(String prayer) {
    String? earliestKey;
    int earliestYear = 9999;
    int earliestMonth = 12;

    prayerData.forEach((key, data) {
      if (data.prayersMissed[prayer]! > 0 &&
          (data.year < earliestYear ||
              (data.year == earliestYear && data.month < earliestMonth))) {
        earliestKey = key;
        earliestYear = data.year;
        earliestMonth = data.month;
      }
    });

    if (earliestKey != null) {
      final data = prayerData[earliestKey]!;
      data.prayersMissed[prayer] = data.prayersMissed[prayer]! - 1;
      data.prayersCompleted[prayer] = data.prayersCompleted[prayer]! + 1;
      data.totalPrayed++;
      data.totalMissed--;
      savePrayerData(data);
    }
  }

  int getCompletedPrayersForMonth(int year, int month, String prayer) {
    final key = '$year-$month';
    return prayerData[key]?.prayersCompleted[prayer] ?? 0;
  }

  int getMissedPrayersForMonth(int year, int month, String prayer) {
    final key = '$year-$month';
    return prayerData[key]?.prayersMissed[prayer] ?? 0;
  }

  Future<PrayerData> getPrayerDataForMonth(int year, int month) async {
    final key = '$year-$month';
    var data = prayerData[key];
    if (data == null) {
      data = PrayerData(
        year: year,
        month: month,
        prayersCompleted: {for (var prayer in prayers) prayer: 0},
        prayersMissed: {for (var prayer in prayers) prayer: 0},
        totalPrayed: 0,
        totalMissed: 0,
        percentagePrayed: 0,
        percentageMissed: 0,
      );
      await savePrayerData(data);
    }
    return data;
  }

  void updatePrayerStats(
      int year, int month, String prayer, bool increaseCompletedCount) {
    final key = '$year-$month';
    final data = prayerData[key];
    if (data != null) {
      if (increaseCompletedCount) {
        data.prayersCompleted[prayer] =
            (data.prayersCompleted[prayer] ?? 0) + 1;
      } else {
        data.prayersMissed[prayer] = (data.prayersMissed[prayer] ?? 0) + 1;
      }
      data.totalPrayed = data.prayersCompleted.values.reduce((a, b) => a + b);
      data.totalMissed = data.prayersMissed.values.reduce((a, b) => a + b);
      data.percentagePrayed =
          (data.totalPrayed / (data.totalPrayed + data.totalMissed)) * 100;
      data.percentageMissed =
          (data.totalMissed / (data.totalPrayed + data.totalMissed)) * 100;
      savePrayerData(data);
    }
  }

  double getPrayerCompletionPercentageForMonth(int year, int month) {
    final key = '$year-$month';
    final data = prayerData[key];
    if (data != null) {
      final totalPrayers = data.totalPrayed + data.totalMissed;
      return totalPrayers > 0 ? (data.totalPrayed / totalPrayers) * 100 : 0;
    }
    return 0;
  }

  double getPrayerCompletionPercentageForYear(int year) {
    double totalPercentage = 0;
    int monthCount = 0;
    for (int month = 1; month <= 12; month++) {
      final key = '$year-$month';
      final data = prayerData[key];
      if (data != null) {
        final totalPrayers = data.totalPrayed + data.totalMissed;
        if (totalPrayers > 0) {
          totalPercentage += (data.totalPrayed / totalPrayers) * 100;
          monthCount++;
        }
      }
    }
    return monthCount > 0 ? totalPercentage / monthCount : 0;
  }

  void updatePrayerStatsForYear(int year, double percentageCompleted) {
    final currentYear = DateTime.now().year;
    final currentMonth = DateTime.now().month;

    for (int month = 1;
        month <= (year == currentYear ? currentMonth - 1 : 12);
        month++) {
      updatePrayerStatsForMonth(year, month, percentageCompleted);
    }
  }

  void updatePrayerStatsForMonth(
      int year, int month, double percentageCompleted) {
    final key = '$year-$month';
    final data = _calculatePrayerData(year, month, percentageCompleted);
    prayerData[key] = data;
    savePrayerData(data);
  }

  void updateStartAndEndYear(int startYear, int endYear) {
    final currentYear = DateTime.now().year;
    final currentMonth = DateTime.now().month;

    for (int year = startYear; year <= endYear; year++) {
      final endMonth = year == currentYear ? currentMonth : 12;
      for (int month = 1; month <= endMonth; month++) {
        final key = '$year-$month';
        if (!prayerData.containsKey(key)) {
          prayerData[key] = _calculatePrayerData(
              year, month, 100 - kDefaultPercentageMissed.toDouble());
        }
      }
    }
    savePrayerData(prayerData.values.first);
  }

  Future<Map<String, dynamic>> getAggregatedPrayerData() async {
    int totalPrayed = 0;
    int totalPossible = 0;

    for (final data in prayerData.values) {
      totalPrayed += data.totalPrayed;
      totalPossible += data.totalPrayed + data.totalMissed;
    }

    return {
      'totalPrayed': totalPrayed,
      'totalPossible': totalPossible,
    };
  }

  void _setDefaultPrayerData() {
    final currentYear = DateTime.now().year;
    final currentMonth = DateTime.now().month;
    prayerData['$currentYear-$currentMonth'] = PrayerData(
      year: currentYear,
      month: currentMonth,
      prayersCompleted: {for (var prayer in prayers) prayer: 0},
      prayersMissed: {for (var prayer in prayers) prayer: 0},
      totalPrayed: 0,
      totalMissed: 0,
      percentagePrayed: 0,
      percentageMissed: 0,
    );
  }

  Future<bool> resetApplication() async {
    prayerData = {};
    _setDefaultPrayerData();
    await _hiveService.clearData();
    return true;
  }

  PrayerData _calculatePrayerData(
      int year, int month, double percentageCompleted) {
    final totalDays = DateTime(year, month + 1, 0).day;
    final totalPrayers = totalDays * prayers.length;
    final completedPrayers = (totalPrayers * percentageCompleted / 100).round();
    final missedPrayers = totalPrayers - completedPrayers;

    final prayersCompleted = {
      for (var prayer in prayers)
        prayer: (totalDays * percentageCompleted / 100).round()
    };
    final prayersMissed = {
      for (var prayer in prayers) prayer: totalDays - prayersCompleted[prayer]!
    };

    return PrayerData(
      year: year,
      month: month,
      prayersCompleted: prayersCompleted,
      prayersMissed: prayersMissed,
      totalPrayed: completedPrayers,
      totalMissed: missedPrayers,
      percentagePrayed: percentageCompleted,
      percentageMissed: 100 - percentageCompleted,
    );
  }

  void sharePrayerData() {
    String shareText = 'My Prayer Progress:\n\n';
    // Implement logic to generate the share text
    Share.share(shareText);
  }
}



