class _YearSelectionScreenState extends State<YearSelectionScreen> {
  late TextEditingController _startYearController;
  late TextEditingController _startMonthController;
  late TextEditingController _endYearController;
  late TextEditingController _endMonthController;
  double _averageMissedPrayers = 25;

  @override
  void initState() {
    super.initState();
    final provider = Provider.of<PrayerProvider>(context, listen: false);
    _startYearController =
        TextEditingController(text: provider.startYear.toString());
    _startMonthController = TextEditingController(text: '1');
    _endYearController =
        TextEditingController(text: provider.endYear.toString());
    _endMonthController =
        TextEditingController(text: DateTime.now().month.toString());
  }

  @override
  void dispose() {
    _startYearController.dispose();
    _startMonthController.dispose();
    _endYearController.dispose();
    _endMonthController.dispose();
    super.dispose();
