            fetch('/static/generated/{os.path.basename(output_dir)}/graph.json')
                .then(response => response.json())
                .then(data => {{
                    var graph = data;
                    var svg = d3.select("svg"),
                        width = +svg.attr("width"),
                        height = +svg.attr("height");

                    var color = d3.scaleOrdinal(d3.schemeCategory10);

                    var simulation = d3.forceSimulation()
                        .force("link", d3.forceLink().id(function(d) {{ return d.id; }}).distance(200))
                        .force("charge", d3.forceManyBody().strength(-500))
                        .force("center", d3.forceCenter(width / 2, height / 2))
                        .force("x", d3.forceX())
                        .force("y", d3.forceY());

                    var link = svg.append("g")
                        .attr("class", "links")
                        .selectAll("line")
                        .data(graph.links)
                        .enter().append("line")
                        .attr("stroke", "black")
                        .attr("stroke-width", function(d) {{ return (d.weight || 3); }})
                        .attr("marker-end", "url(#arrow)")
                        .attr("id", function(d, i) {{ return "link" + i; }});

                    var linkLabel = svg.append("g")
                        .attr("class", "link-labels")
                        .selectAll("text")
                        .data(graph.links)
                        .enter().append("text")
                        .attr("font-size", 10)
                        .attr("fill", "black")
                        .attr("dy", ".35em");

                    linkLabel.append("textPath")
                        .attr("xlink:href", function(d, i) {{ return "#link" + i; }})
                        .attr("startOffset", "50%")
                        .text(function(d) {{ return d.title; }});

                    var node = svg.append("g")
                        .attr("class", "nodes")
                        .selectAll("circle")
                        .data(graph.nodes)
                        .enter().append("circle")
                        .attr("r", 10)
                        .attr("fill", function(d) {{ return color(d.group); }})
                        .call(d3.drag()
                            .on("start", dragstarted)
                            .on("drag", dragged)
                            .on("end", dragended))
                        .on("click", click);

                    node.append("title")
                        .text(function(d) {{ return d.id; }});

                    var labels = svg.append("g")
                        .attr("class", "labels")
                        .selectAll("text")
                        .data(graph.nodes)
                        .enter().append("text")
                        .attr("class", "label")
                        .attr("dx", 12)
                        .attr("dy", ".35em")
                        .text(function(d) {{ return d.id; }});

                    simulation
                        .nodes(graph.nodes)
                        .on("tick", ticked);

                    simulation.force("link")
                        .links(graph.links);

                    graph.nodes.forEach(function(d) {{
                        d.x = Math.random() * width;
                        d.y = Math.random() * height;
                    }});

                    var tooltip = d3.select("#tooltip");

                    function ticked() {{
                        link
                            .attr("x1", function(d) {{ return d.source.x; }})
                            .attr("y1", function(d) {{ return d.source.y; }})
                            .attr("x2", function(d) {{ return d.target.x; }})
                            .attr("y2", function(d) {{ return d.target.y; }});

                        node
                            .attr("cx", function(d) {{ return d.x; }})
                            .attr("cy", function(d) {{ return d.y; }});

                        labels
                            .attr("x", function(d) {{ return d.x; }})
                            .attr("y", function(d) {{ return d.y; }});

                        linkLabel
                            .attr("transform", function(d) {{
                                var angle = Math.atan((d.target.y - d.source.y) / (d.target.x - d.source.x)) * 180 / Math.PI;
                                return 'translate(' + [(d.source.x + d.target.x) / 2, (d.source.y + d.target.y) / 2] + ')rotate(' + angle + ')';
                            }});
                    }}

                    function click(event, d) {{
                        tooltip.style("visibility", "hidden");
                        d3.selectAll(".nodes circle").classed("selected", false);
                        d3.select(this).classed("selected", true);

                        var links = graph.links.filter(function(l) {{ return l.source.id === d.id || l.target.id === d.id; }});

                        var tooltipHtml = links.map(function(l) {{
                            return '<p>' + l.source.id + ' → ' + l.target.id + ': ' + l.title + '</p>';
                        }}).join("");

                        tooltip.html(tooltipHtml)
                            .style("left", (event.pageX + 10) + "px")
                            .style("top", (event.pageY + 10) + "px")
                            .style("visibility", "visible");

                        event.stopPropagation(); // Prevents the click event from bubbling up to the SVG element
                    }}

                    svg.on("click", function() {{
                        tooltip.style("visibility", "hidden");
                        d3.selectAll(".nodes circle").classed("selected", false);
                    }});

                    function dragstarted(event, d) {{
                        if (!event.active) simulation.alphaTarget(0.3).restart();
                        d.fx = d.x;
                        d.fy = d.y;
                    }}

                    function dragged(event, d) {{
                        d.fx = event.x;
                        d.fy = event.y;
                    }}

                    function dragended(event, d) {{
                        if (!event.active) simulation.alphaTarget(0);
                        d.fx = null;
                        d.fy = null;
                    }}
                }});
        </script>
    </body>
    </html>
    """

    graph_html_path = os.path.join(output_dir, 'index.html')
    with open(graph_html_path, 'w') as file:
        file.write(html_content)

    # Saving as PyVis HTML
    net = Network(
        notebook=False,
        cdn_resources="remote",
        height="900px",
        width="100%",
        select_menu=True,
        filter_menu=False,
    )

    net.from_nx(G)
    net.force_atlas_2based(central_gravity=0.015, gravity=-31)
    net.show_buttons(filter_=["physics"])
    pyvis_output_file = os.path.join(output_dir, 'pyvis.html')
    net.save_graph(pyvis_output_file)

