    for edge in all_edges:
        weight = edge.weight if hasattr(edge, 'weight') else 1
        graph.add_edge(str(edge.node_1.name), str(edge.node_2.name), title=edge.relationship, weight=weight)

    communities_generator = nx.community.girvan_newman(graph)
    top_level_communities = next(communities_generator)
    try:
        next_level_communities = next(communities_generator)
        communities = sorted(map(sorted, next_level_communities))
    except StopIteration:
        communities = sorted(map(sorted, top_level_communities))

    color_palette = "hls"
    colors = sns.color_palette(color_palette, len(communities)).as_hex()
    random.shuffle(colors)
    rows = []
    group = 0

    for community in communities:
        color = colors.pop()
        group += 1
        for node in community:
            rows.append({"node": node, "color": color, "group": group})

    df_colors = pd.DataFrame(rows)

    for _, row in df_colors.iterrows():
        graph.nodes[row['node']]['group'] = row['group']
        graph.nodes[row['node']]['color'] = row['color']
        graph.nodes[row['node']]['size'] = graph.degree[row['node']]

